  templateUrl: './scripts.component.html',
  styleUrls: ['./scripts.component.scss'],
  imports: [NgFor, RouterLink, FontAwesomeModule, ModalComponent],
})
export class ScriptsComponent implements OnInit, AfterViewChecked {
  @ViewChild('modalContainer', { read: ViewContainerRef })
  container!: ViewContainerRef;

  private initialStatusSet = false;

  faTrash = faTrash;
  faUpload = faUpload;
  faRun = faPlay;
  faCopy = faCopy;

  _scripts: Script[] = new Array<Script>();

  scripts: Script[];

  locations = [
    AstrOsConstants.BODY,
    AstrOsConstants.CORE,
    AstrOsConstants.DOME,
  ];

  constructor(
    private router: Router,
    private scriptService: ScriptsService,
    private socket: WebsocketService,
    private snackBarService: SnackbarService,
    private modalService: ModalService,
  ) {
    this.scripts = new Array<Script>();

    this.socket.messages.subscribe((msg: unknown) => {
      if (msg && typeof msg === 'object' && 'type' in msg)
        if (msg.type === TransmissionType.script) {
          this.statusUpdate(msg as ScriptResponse);
        }
    });
  }

  ngOnInit(): void {
    const observer = {
      next: (result: Script[]) => {
        this.scripts = result.sort((a, b) => {
          if (a.scriptName > b.scriptName) {
            return 1;
          }
          if (a.scriptName < b.scriptName) {
            return -1;
          }
          return 0;
        });
      },
      error: (err: unknown) => console.error(err),
    };

    this.scriptService.getAllScripts().subscribe(observer);
  }

  ngAfterViewChecked() {
    if (this.initialStatusSet) {
      return;
    }
    if (this.scripts.length === 0) {
      return;
    }

    for (const script of this.scripts) {
      this.updateUploadStatusElement(AstrOsConstants.BODY, script.id);
      this.updateUploadStatusElement(AstrOsConstants.CORE, script.id);
      this.updateUploadStatusElement(AstrOsConstants.DOME, script.id);
    }

    this.initialStatusSet = true;
  }

  updateUploadStatusElement(locationId: string, scriptId: string): void {
    const el = document.getElementById(`${scriptId}_${locationId}`);
    if (el === null) {
      return;
    }
    const status = this.getUploadStatus(scriptId, locationId);
    el.classList.remove('uploaded');
    el.classList.remove('notuploaded');
    el.classList.remove('uploading');
    el.classList.add(status.s);

    const toolTip = document.getElementById(
      `${scriptId}_${locationId}_tooltip`,
    );
    if (toolTip === null) {
      return;
    }
    toolTip.innerText = status.d;
  }

  setUploadingStatus(scriptId: string): void {
    const script = this.getScript(scriptId);

    if (!script) {
      return;
    }

    for (const location of this.locations) {
      if (
        script.deploymentStatusKvp
          .map((s) => {
            return s.key;
          })
          .indexOf(location) > -1
      ) {
        const el = document.getElementById(`${scriptId}_${location}`);
        if (el === null) {
          continue;
        }
        el.classList.remove('uploaded');
        el.classList.remove('notuploaded');
        el.classList.add('uploading');

        const toolTip = document.getElementById(
          `${scriptId}_${location}_tooltip`,
        );
        if (toolTip === null) {
          continue;
        }
        toolTip.innerText = 'Uploading...';
      }
    }
  }

  newScript() {
    this.router.navigate(['scripter', '0']);
  }

  modalCallback(evt: ModalCallbackEvent) {
    switch (evt.type) {
      case ConfirmModalResources.confirmEvent: {
        {
          const evtData = evt.value as DeleteConfirmEvent;
          if (evtData.id === 'script_delete') {
            this.removeFile(evtData.val);
          }
          break;
        }
      }
    }

    this.modalService.close('scripts-modal');
    this.container.clear();
  }

  removeClicked(id: string) {
    this.container.clear();

    const modalResources = new Map<string, unknown>();
    modalResources.set(ConfirmModalResources.action, 'Delete');
    modalResources.set(
      ConfirmModalResources.message,
      `Are you sure you want to delete script?`,
    );
    modalResources.set(ConfirmModalResources.confirmEvent, {
      id: 'script_delete',
      val: id,
    });

    const component = this.container.createComponent(ConfirmModalComponent);

    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
      this.modalCallback(evt);
    });

    this.modalService.open('scripts-modal');
  }

  removeFile(id: string) {
    const idx = this.scripts
      .map((s) => {
        return s.id;
      })
      .indexOf(id);

    if (idx < 0) {
      return;
    }

    this.scripts.splice(idx, 1);

    this.scriptService.deleteScript(id).subscribe();
  }

  runClicked(id: string) {
    const idx = this.scripts
      .map((s) => {
        return s.id;
      })
      .indexOf(id);

    if (idx < 0) {
      return;
    }

    const observer = {
      next: (result: unknown) => {
        console.log(result);
        this.snackBarService.okToast('Script run queued!');
      },
      error: (err: unknown) => {
        console.error(err);
        this.snackBarService.okToast('Error requesting upload. Check logs.');
      },
    };

    this.scriptService.runScript(id).subscribe(observer);
  }

  copyClicked(id: string) {
    const idx = this.scripts
      .map((s) => {
        return s.id;
      })
      .indexOf(id);

    if (idx < 0) {
      return;
    }

    const observer = {
      next: (result: Script) => {
        if (result === undefined) {
          this.snackBarService.okToast('Error copying script. Check logs.');
        } else {
          this.scripts.push(result);

          this.scripts.sort((a, b) => {
            if (a.scriptName > b.scriptName) {
              return 1;
            }
            if (a.scriptName < b.scriptName) {
              return -1;
            }
            return 0;
          });
        }
      },
      error: (err: unknown) => {
        console.error(err);
        this.snackBarService.okToast('Error copying script. Check logs.');
      },
    };

    this.scriptService.copyScript(id).subscribe(observer);
  }

  uploadClicked(id: string) {
    const observer = {
      next: (result: unknown) => console.log(result),
      error: (err: unknown) => {
        console.error(err);
        this.snackBarService.okToast('Error requesting upload. Check logs.');
      },
    };

    const idx = this.scripts
      .map((s) => {
        return s.id;
      })
      .indexOf(id);

    if (idx < 0) {
      return;
    }

    this.scriptService.uploadScript(id).subscribe(observer);
    this.setUploadingStatus(id);
  }

  statusUpdate(msg: ScriptResponse) {
    if (msg.status === TransmissionStatus.success) {
      this.setUploadDate(msg.scriptId, msg.locationId, msg.date);
    }

    this.updateUploadStatusElement(msg.locationId, msg.scriptId);
  }

  getUploadStatus(id: string, locationId: string): { s: string; d: string } {
    let dateString = 'Not Uploaded';

    const script = this.getScript(id);

    if (!script) {
      return { s: 'notuploaded', d: dateString };
    }

    const status = script.deploymentStatusKvp.find((s) => {
      return s.value.locationName === locationId;
    });

    if (!status) {
      return { s: 'notuploaded', d: dateString };
    }

    let uploadStatus = status.value.value;

    if (status.value.date) {
      const uploaddate = new Date(status.value.date);
      const scriptdate = new Date(script.lastSaved);
      if (uploaddate < scriptdate) {
        uploadStatus = UploadStatus.notUploaded;
        dateString = ' Out of date ';
      } else {
        dateString = uploaddate.toLocaleDateString(navigator.language, {
          year: 'numeric',
          month: 'numeric',
          day: 'numeric',
          hour: 'numeric',
          minute: 'numeric',
        });
      }
    }

    return { s: this.getUploadStatusClass(uploadStatus), d: dateString };
  }

  getUploadStatusClass(status: UploadStatus) {
    switch (status) {
      case UploadStatus.notUploaded:
        return 'notuploaded';
      case UploadStatus.uploading:
        return 'uploading';
      case UploadStatus.uploaded:
        return 'uploaded';
      default:
        return 'notuploaded';
    }
  }

  setUploadDate(id: string, controllerId: string, date: Date): void {
    const script = this.getScript(id);

    if (!script) {
      return;
    }

    const sidx = script.deploymentStatusKvp
      .map((s) => {
        return s.key;
      })
      .indexOf(controllerId.toString());

    if (sidx < 0) {
      return;
    }

    script.deploymentStatusKvp[sidx].value.date = date;
  }

  getScript(id: string): Script | undefined {
    const idx = this.scripts
      .map((s) => {
        return s.id;
      })
      .indexOf(id);

    if (idx < 0) {
      return undefined;
    }

    return this.scripts[idx];
  }
}

import { Component, OnInit } from '@angular/core';
import {
  AstrOsConstants,
  ScriptResponse,
  TransmissionStatus,
  TransmissionType,
} from 'astros-common';
import { ScriptsService } from 'src/app/services/scripts/scripts.service';
import { WebsocketService } from 'src/app/services/websocket/websocket.service';
import { BaseEventModalComponent } from '../base-event-modal/base-event-modal.component';

interface Caption {
  str: string;
}

export interface DeploymentLocation {
  id: string;
  name: string;
}

export class ScriptTestModalResources {
  public static scriptId = 'scriptId';
  public static locations = 'locations';
}

@Component({
  selector: 'app-script-test-modal',
  templateUrl: './script-test-modal.component.html',
  styleUrls: [
    '../base-event-modal/base-event-modal.component.scss',
    './script-test-modal.component.scss',
  ],
  standalone: true,
})
export class ScriptTestModalComponent
  extends BaseEventModalComponent
  implements OnInit
{
  uploadInProgress = true;
  runDisabled = true;

  deploymentLocations: DeploymentLocation[] = [];

  coreUpload: TransmissionStatus = TransmissionStatus.sending;
  domeUpload: TransmissionStatus = TransmissionStatus.sending;
  bodyUpload: TransmissionStatus = TransmissionStatus.sending;

  coreCaption: Caption = { str: 'Uploading' };
  domeCaption: Caption = { str: 'Uploading' };
  bodyCaption: Caption = { str: 'Uploading' };

  status: string;

  scriptId = '';

  constructor(
    private socket: WebsocketService,
    private scriptService: ScriptsService,
  ) {
    super();
    this.status = 'Uploading script...';

    this.socket.messages.subscribe((msg: unknown) => {
      if (msg && typeof msg === 'object' && 'type' in msg) {
        if (msg.type === TransmissionType.script) {
          this.statusUpdate(msg as ScriptResponse);
        }
      }
    });
  }

  ngOnInit(): void {
    this.scriptId = this.resources.get(
      ScriptTestModalResources.scriptId,
    ) as string;
    this.deploymentLocations = this.resources.get(
      ScriptTestModalResources.locations,
    ) as DeploymentLocation[];

    let hasBody = false;
    let hasCore = false;
    let hasDome = false;

    this.deploymentLocations.forEach((location: DeploymentLocation) => {
      switch (location.name) {
        case AstrOsConstants.BODY:
          hasBody = true;
          break;
        case AstrOsConstants.CORE:
          hasCore = true;
          break;
        case AstrOsConstants.DOME:
          hasDome = true;
          break;
      }
    });

    this.setInitialUploadStatus(hasBody, hasCore, hasDome);

    const observer = {
      next: (result: unknown) => console.log(result),
      error: (err: unknown) => {
        console.error(err);
        this.status = 'Error requesting Script Upload';
        this.coreUpload = TransmissionStatus.failed;
        this.coreCaption.str = 'Failed';
        this.domeUpload = TransmissionStatus.failed;
        this.domeCaption.str = 'Failed';
        this.bodyUpload = TransmissionStatus.failed;
        this.bodyCaption.str = 'Failed';
      },
    };

    if (this.scriptId != '') {
      this.scriptService.uploadScript(this.scriptId).subscribe(observer);
    } else {
      this.status = 'Script ID missing, close dialog to continue.';
    }
  }

  runClicked() {
    console.log(`Running script: ${this.scriptId}`);
    this.scriptService.runScript(this.scriptId).subscribe();
    this.closeModal();
  }

  setInitialUploadStatus(hasBody: boolean, hasCore: boolean, hasDome: boolean) {
    if (hasBody) {
      this.bodyUpload = TransmissionStatus.sending;
      this.bodyCaption.str = 'Uploading';
    } else {
      this.bodyUpload = TransmissionStatus.success;
      this.bodyCaption.str = 'Not Assigned';
    }

    if (hasCore) {
      this.coreUpload = TransmissionStatus.sending;
      this.coreCaption.str = 'Uploading';
    } else {
      this.coreUpload = TransmissionStatus.success;
      this.coreCaption.str = 'Not Assigned';
    }

    if (hasDome) {
      this.domeUpload = TransmissionStatus.sending;
      this.domeCaption.str = 'Uploading';
    } else {
      this.domeUpload = TransmissionStatus.success;
      this.domeCaption.str = 'Not Assigned';
    }
  }

  statusUpdate(msg: ScriptResponse) {
    console.log('message', msg);

    const location = this.deploymentLocations.find(
      (loc) => loc.id === msg.locationId,
    );

    if (!location) {
      console.warn(`Location with ID ${msg.locationId} not found.`);
      return;
    }

    switch (location.name) {
      case AstrOsConstants.BODY:
        this.bodyUpload = msg.status;
        this.setCaption(this.bodyCaption, msg.status);
        break;
      case AstrOsConstants.CORE:
        this.coreUpload = msg.status;
        this.setCaption(this.coreCaption, msg.status);
        break;
      case AstrOsConstants.DOME:
        this.domeUpload = msg.status;
        this.setCaption(this.domeCaption, msg.status);
        break;
    }

    if (this.coreUpload > 1 && this.domeUpload > 1 && this.bodyUpload > 1) {
      this.status = 'Upload Complete.';
      this.uploadInProgress = false;
      if (this.coreUpload + this.domeUpload + this.bodyUpload >= 6) {
        this.runDisabled = false;
      }
    }
  }

  setCaption(caption: Caption, status: TransmissionStatus) {
    switch (status) {
      case TransmissionStatus.success:
        caption.str = 'Success';
        break;
      case TransmissionStatus.failed:
        caption.str = 'Failed';
        break;
    }
  }
}

import { Component, OnInit } from '@angular/core';
import {
  BaseEventModalComponent,
  ScriptEventModalResources,
} from '../base-event-modal/base-event-modal.component';
import {
  HcrCommand,
  HcrCommandCategory,
  HumanCyborgRelationsCmd,
  HumanCyborgRelationsEvent,
  HumanCyborgRelationsModule,
  ScriptEvent,
} from 'astros-common';
import { faBan } from '@fortawesome/free-solid-svg-icons';
import { FormsModule } from '@angular/forms';
import { NgFor, DecimalPipe } from '@angular/common';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { ModalCallbackEvent } from '../../modal-base/modal-callback-event';

interface HcrCommandListItem {
  id: HumanCyborgRelationsCmd;
  name: string;
}

@Component({
  selector: 'app-human-cyborg-modal',
  templateUrl: './human-cyborg-modal.component.html',
  styleUrls: [
    '../base-event-modal/base-event-modal.component.scss',
    './human-cyborg-modal.component.scss',
  ],
  imports: [FormsModule, NgFor, FontAwesomeModule, DecimalPipe],
})
export class HumanCyborgModalComponent
  extends BaseEventModalComponent
  implements OnInit
{
  faRemove = faBan;

  commandCategory: string;
  command!: string;
  valueA!: string;
  valueB!: string;

  selectedCommands: HcrCommand[];
  commands: HcrCommandListItem[];

  hasValueA: HumanCyborgRelationsCmd[];
  hasValueB: HumanCyborgRelationsCmd[];

  constructor() {
    super();
    this.originalEventTime = 0;
    this.eventTime = 0;

    this.selectedCommands = new Array<HcrCommand>();
    this.commands = new Array<HcrCommandListItem>();
    this.commandCategory = HcrCommandCategory.stimuli.toString();
    this.setAvailableCommands(+this.commandCategory);

    this.hasValueA = new Array<HumanCyborgRelationsCmd>();
    this.hasValueA.push(
      HumanCyborgRelationsCmd.minSecondsBetweenMusings,
      HumanCyborgRelationsCmd.maxSecondsBetweenMusings,
      HumanCyborgRelationsCmd.playWavOnA,
      HumanCyborgRelationsCmd.playWavOnB,
      HumanCyborgRelationsCmd.vocalizerVolume,
      HumanCyborgRelationsCmd.wavAVolume,
      HumanCyborgRelationsCmd.wavBVolume,
      HumanCyborgRelationsCmd.setHappyLevel,
      HumanCyborgRelationsCmd.setSadLevel,
      HumanCyborgRelationsCmd.setMadLevel,
      HumanCyborgRelationsCmd.setScaredLevel,
    );

    this.hasValueB = new Array<HumanCyborgRelationsCmd>();
    this.hasValueB.push(
      HumanCyborgRelationsCmd.playSdRandomOnA,
      HumanCyborgRelationsCmd.playSdRandomOnB,
    );

    this.errorMessage = '';
    this.callbackType = ScriptEventModalResources.addEvent;
  }

  ngOnInit(): void {
    if (this.resources.has(ScriptEventModalResources.callbackType)) {
      this.callbackType = this.resources.get(
        ScriptEventModalResources.callbackType,
      ) as string;
    }

    if (this.callbackType === ScriptEventModalResources.editEvent) {
      const element = document.getElementById('remove_button');
      element?.classList.remove('hidden');
    }

    this.scriptEvent = this.resources.get(
      ScriptEventModalResources.scriptEvent,
    ) as ScriptEvent;

    const temp = this.scriptEvent.event as HumanCyborgRelationsEvent;

    if (temp !== undefined) {
      this.selectedCommands.push(...temp.commands);
    }

    this.originalEventTime = this.scriptEvent.time / this.timeFactor;
    this.eventTime = this.scriptEvent.time / this.timeFactor;
  }

  categoryChange(_: unknown) {
    this.errorMessage = '';
    this.setAvailableCommands(+this.commandCategory);
  }

  commandChange(_: unknown) {
    this.errorMessage = '';
    if (this.hcrHasBValue(+this.command)) {
      document.getElementById('value-a')?.removeAttribute('disabled');
      document.getElementById('value-b')?.removeAttribute('disabled');
      return;
    }
    if (this.hcrHasAValue(+this.command)) {
      document.getElementById('value-a')?.removeAttribute('disabled');
      document.getElementById('value-b')?.setAttribute('disabled', 'disabled');
      return;
    }

    document.getElementById('value-a')?.setAttribute('disabled', 'disabled');
    document.getElementById('b')?.setAttribute('disabled', 'disabled');
  }

  setAvailableCommands(category: HcrCommandCategory) {
    this.commands.splice(0);

    switch (category) {
      case HcrCommandCategory.stimuli:
        this.commands.push(
          this.hcrListItem(HumanCyborgRelationsCmd.mildHappy),
          this.hcrListItem(HumanCyborgRelationsCmd.extremeHappy),
          this.hcrListItem(HumanCyborgRelationsCmd.mildSad),
          this.hcrListItem(HumanCyborgRelationsCmd.extremeSad),
          this.hcrListItem(HumanCyborgRelationsCmd.mildAngry),
          this.hcrListItem(HumanCyborgRelationsCmd.extremeAngry),
          this.hcrListItem(HumanCyborgRelationsCmd.mildScared),
          this.hcrListItem(HumanCyborgRelationsCmd.extremeScared),
          this.hcrListItem(HumanCyborgRelationsCmd.overload),
        );
        break;
      case HcrCommandCategory.muse:
        this.commands.push(
          this.hcrListItem(HumanCyborgRelationsCmd.enableMuse),
          this.hcrListItem(HumanCyborgRelationsCmd.disableMuse),
          this.hcrListItem(HumanCyborgRelationsCmd.toggleMuse),
          this.hcrListItem(HumanCyborgRelationsCmd.triggerMusing),
          this.hcrListItem(HumanCyborgRelationsCmd.minSecondsBetweenMusings),
          this.hcrListItem(HumanCyborgRelationsCmd.maxSecondsBetweenMusings),
        );
        break;
      case HcrCommandCategory.sdWav:
        this.commands.push(
          this.hcrListItem(HumanCyborgRelationsCmd.playWavOnA),
          this.hcrListItem(HumanCyborgRelationsCmd.playWavOnB),
          this.hcrListItem(HumanCyborgRelationsCmd.playSdRandomOnA),
          this.hcrListItem(HumanCyborgRelationsCmd.playSdRandomOnB),
        );
        break;
      case HcrCommandCategory.stop:
        this.commands.push(
          this.hcrListItem(HumanCyborgRelationsCmd.panicStop),
          this.hcrListItem(HumanCyborgRelationsCmd.gracefulStop),
          this.hcrListItem(HumanCyborgRelationsCmd.stopWavOnA),
          this.hcrListItem(HumanCyborgRelationsCmd.stopWavOnB),
        );
        break;
      case HcrCommandCategory.volume:
        this.commands.push(
          this.hcrListItem(HumanCyborgRelationsCmd.vocalizerVolume),
          this.hcrListItem(HumanCyborgRelationsCmd.wavAVolume),
          this.hcrListItem(HumanCyborgRelationsCmd.wavBVolume),
        );
        break;
      case HcrCommandCategory.override:
        this.commands.push(
          this.hcrListItem(HumanCyborgRelationsCmd.enableImprov),
          this.hcrListItem(HumanCyborgRelationsCmd.enableCanonical),
          this.hcrListItem(HumanCyborgRelationsCmd.enablePersonalityOverride),
          this.hcrListItem(HumanCyborgRelationsCmd.disablePersonalityOverride),
          this.hcrListItem(HumanCyborgRelationsCmd.zeroEmotions),
          this.hcrListItem(HumanCyborgRelationsCmd.setHappyLevel),
          this.hcrListItem(HumanCyborgRelationsCmd.setSadLevel),
          this.hcrListItem(HumanCyborgRelationsCmd.setMadLevel),
          this.hcrListItem(HumanCyborgRelationsCmd.setScaredLevel),
        );
        break;
    }

    if (this.commands.length > 0) {
      this.command = this.commands[0].id.toString();
    }
  }

  addCommand() {
    let missingA = false;
    let missingB = false;

    if (this.hcrHasBValue(+this.command)) {
      if (this.valueA === undefined || this.valueA === null) {
        missingA = true;
      }
      if (this.valueB === undefined || this.valueB === null) {
        missingB = true;
      }
    }

    if (this.hcrHasAValue(+this.command)) {
      if (this.valueA === undefined || this.valueA === null) {
        missingA = true;
      }
    }

    if (missingA || missingB) {
      this.errorMessage = `Required Values Missing: ${missingA ? 'A' : ''}${missingA && missingB ? ',' : ''}${missingB ? 'B' : ''}`;
      return;
    }

    this.selectedCommands.push(
      new HcrCommand(
        crypto.randomUUID().toString(),
        +this.commandCategory,
        +this.command,
        +this.valueA,
        +this.valueB,
      ),
    );
  }

  removeCommand(id: string) {
    const cmdIdx = this.selectedCommands?.findIndex((x) => x.id === id);
    if (cmdIdx != undefined && cmdIdx > -1) {
      this.selectedCommands.splice(cmdIdx, 1);
    }
  }

  addEvent() {
    if (+this.eventTime > this.maxTime) {
      this.errorMessage = `Event time cannot be larger than ${this.maxTime / this.timeFactor}`;
      return;
    }

    this.scriptEvent.time = +this.eventTime * this.timeFactor;

    const data = new HumanCyborgRelationsEvent(this.selectedCommands);

    this.scriptEvent.event = data;

    const evt = new ModalCallbackEvent(this.callbackType, {
      scriptEvent: this.scriptEvent,
      time: this.originalEventTime,
    });
    this.modalCallback.emit(evt);
  }

  formatSelectedCommand(cmd: HcrCommand) {
    if (this.hcrHasBValue(cmd.command)) {
      return `${this.hcrName(cmd.command)}: ${cmd.valueA} ${cmd.valueB}`;
    }

    if (this.hcrHasAValue(cmd.command)) {
      return `${this.hcrName(cmd.command)}: ${cmd.valueA}`;
    }

    return this.hcrName(cmd.command);
  }

  hcrListItem(cmd: HumanCyborgRelationsCmd) {
    return { id: cmd, name: this.hcrName(cmd) };
  }

  hcrName(cmd: HumanCyborgRelationsCmd) {
    return HumanCyborgRelationsModule.getCommandName(cmd);
  }

  hcrHasBValue(cmd: HumanCyborgRelationsCmd) {
    return this.hasValueB.find((x) => x === cmd) !== undefined;
  }

  hcrHasAValue(cmd: HumanCyborgRelationsCmd) {
    return this.hasValueA.find((x) => x === cmd) !== undefined;
  }
}

import { Component, OnInit } from '@angular/core';
import { WebsocketService } from 'src/app/services/websocket/websocket.service';
import { ControllerService } from '@src/services';
import {
  AstrOsLocationCollection,
  ControllersResponse,
  ControlModule,
  TransmissionType,
} from 'astros-common';
import { Subscription } from 'rxjs';
import { ModalCallbackEvent } from '../..//modal-base/modal-callback-event';
import { ModalBaseComponent } from '../../modal-base/modal-base.component';

export class LoadingModalResources {
  public static skipControllerLoading = 'loading_skipControllerLoading';
  public static closeEvent = 'loading_closeEvent';
}

export interface LoadingModalResponse {
  controllers: ControlModule[];
  locations: AstrOsLocationCollection;
}

@Component({
  selector: 'app-loading-modal',
  templateUrl: './loading-modal.component.html',
  styleUrls: ['./loading-modal.component.scss'],
  standalone: true,
})
export class LoadingModalComponent
  extends ModalBaseComponent
  implements OnInit
{
  subscription!: Subscription;

  message = 'Loading Controllers...';
  controllersMsg = TransmissionType.controllers;
  locations!: AstrOsLocationCollection;
  controllers!: ControllersResponse;

  locationsLoaded = false;
  controllersLoaded = false;

  disableButton = true;

  constructor(
    private socket: WebsocketService,
    private controllerService: ControllerService,
  ) {
    super();
  }

  ngOnInit(): void {
    if (this.resources.has(LoadingModalResources.skipControllerLoading)) {
      this.message = 'Skipping controller loading...';
      this.controllers = {
        type: TransmissionType.controllers,
        success: false,
        controllers: [new ControlModule('test', 'master', '00:00:00:00:00:00')],
        message: 'Skipping controller loading',
      };
      this.controllersLoaded = true;
    } else {
      const observer = {
        next: (_: unknown) => {
          console.log('Synced controllers');
        },
        error: (err: unknown) => console.error(err),
      };

      this.controllerService.syncControllers().subscribe(observer);

      this.subscription = this.socket.messages.subscribe((msg: unknown) => {
        console.log('Received message', msg);
        if (msg && typeof msg === 'object' && 'type' in msg)
          if (msg.type === this.controllersMsg) {
            this.controllers = msg as ControllersResponse;
            this.controllersLoaded = true;
            this.checkLoadedState();
          }
      });
    }

    const locationsObserver = {
      next: (result: AstrOsLocationCollection) => {
        console.log('Loaded locations');
        this.locations = result;
        this.locationsLoaded = true;
        this.checkLoadedState();
      },
      error: (err: unknown) => console.error(err),
    };

    this.controllerService.getLoadedLocations().subscribe(locationsObserver);
  }

  checkLoadedState() {
    if (this.locationsLoaded && this.controllersLoaded) {
      if (this.controllers.success) {
        this.closeModal();
      } else {
        this.disableButton = false;
        this.message = 'Failed to load controllers, using cached values.';
      }
    }
  }

  closeModal(): void {
    this.subscription?.unsubscribe();
    const evt = new ModalCallbackEvent(LoadingModalResources.closeEvent, {
      controllers: this.controllers.controllers,
      locations: this.locations,
    });

    this.modalCallback.emit(evt);
  }
}

import { ScriptChannelType } from 'astros-common';

export interface LocationDetails {
  id: string;
  name: string;
  assigned: boolean;
}

export interface ChannelDetails {
  id: string;
  name: string;
  locationId: string;
  available: boolean;
  scriptChannelType: ScriptChannelType;
}

import {
  AfterViewChecked,
  Component,
  ComponentRef,
  ElementRef,
  OnInit,
  Renderer2,
  ViewChild,
  ViewContainerRef,
} from '@angular/core';
import {
  MatMenuTrigger,
  MatMenu,
  MatMenuContent,
  MatMenuItem,
} from '@angular/material/menu';
import { ActivatedRoute } from '@angular/router';
import { Guid } from 'guid-typescript';
import {
  Script,
  ScriptChannel,
  ScriptEvent,
  ModuleType,
  ModuleSubType,
  ScriptChannelType,
  ModuleChannelTypes,
  MaestroChannel,
} from 'astros-common';
import EventMarkerHelper from './helper/event-marker-helper';
import { FormsModule } from '@angular/forms';
import { NgFor, NgIf } from '@angular/common';
import {
  ModuleChannelChangedEvent,
  ScriptRowComponent,
  TimeLineEvent,
} from '@src/components/scripting';
import {
  AudioEventModalComponent,
  ChannelTestModalComponent,
  ChannelTestModalResources,
  ChannelTestModalResponse,
  AddChannelModalComponent,
  AddChannelModalResources,
  AddChannelModalResponse,
  GpioEventModalComponent,
  HumanCyborgModalComponent,
  I2cEventModalComponent,
  KangarooEventModalComponent,
  KangarooEventModalResources,
  ScriptEventModalResources,
  ScriptEventModalResponse,
  ScriptTestModalComponent,
  ScriptTestModalResources,
  UartEventModalComponent,
  ServoEventModalComponent,
  DeploymentLocation,
} from '@src/components/modals/scripting';
import {
  ControllerService,
  ModalService,
  ScriptsService,
  SnackbarService,
} from '@src/services';
import {
  ConfirmModalComponent,
  ConfirmModalEvent,
  ConfirmModalResources,
  ModalComponent,
} from '@src/components/modals';
import { ModalCallbackEvent } from '@src/components/modals/modal-base/modal-callback-event';
import { ScriptResourcesService } from '@src/services/script-resources/script-resources.service';
import { ChannelDetails } from '@src/models/scripting';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { ScrollingModule } from '@angular/cdk/scrolling';
import { DragDropModule } from '@angular/cdk/drag-drop';

export interface MenuItemDetails {
  timeline: string;
  xPos: number;
}

type ScripterModal =
  | UartEventModalComponent
  | KangarooEventModalComponent
  | I2cEventModalComponent
  | AudioEventModalComponent
  | GpioEventModalComponent
  | HumanCyborgModalComponent
  | ServoEventModalComponent;

@Component({
  selector: 'app-scripter',
  templateUrl: './scripter.component.html',
  styleUrls: ['./scripter.component.scss'],
  imports: [
    FormsModule,
    NgFor,
    NgIf,
    ScriptRowComponent,
    MatMenuTrigger,
    MatMenu,
    MatMenuContent,
    MatMenuItem,
    ModalComponent,
    MatProgressSpinnerModule,
    ScrollingModule,
    DragDropModule,
  ],
})
export class ScripterComponent implements OnInit, AfterViewChecked {
  @ViewChild(MatMenuTrigger)
  menuTrigger!: MatMenuTrigger;

  @ViewChild('modalContainer', { read: ViewContainerRef })
  container!: ViewContainerRef;

  @ViewChild('scripterContainer')
  scripterContainer!: ElementRef;
  isDragging = false;
  dragStartX = 0;
  scrollLeft = 0;
  dragStartY = 0;
  scrollTop = 0;

  private segmentWidth = 60;
  private segments = 3000;
  private segmentFactor = 10;
  private scriptId: string;
  private renderedEvents = false;
  private characters =
    'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  private removeChannelEvent = 'scripter_removeChannel';

  backgroundClickDisabled = '0';

  script!: Script;
  scriptChannels: ScriptChannel[];

  timeLineArray: number[];
  menuTopLeft = { x: 0, y: 0 };

  components: unknown[];

  constructor(
    private route: ActivatedRoute,
    private snackBar: SnackbarService,
    private modalService: ModalService,
    private renderer: Renderer2,
    private controllerService: ControllerService,
    private scriptService: ScriptsService,
    public scriptResources: ScriptResourcesService,
  ) {
    this.scriptId = this.route.snapshot.paramMap.get('id') ?? '0';

    this.timeLineArray = Array.from(
      { length: this.segments },
      (_, i) => (i + 1) / this.segmentFactor,
    );

    this.scriptChannels = new Array<ScriptChannel>();
    this.components = new Array<unknown>();
  }

  ngAfterViewChecked(): void {
    // "this" needs to exist before we render events
    if (!this.renderedEvents) {
      if (this.script != undefined) {
        for (const ch of this.script.scriptChannels) {
          for (const kvp of ch.eventsKvpArray) {
            this.renderEvent(kvp.value);
          }
        }
        this.renderedEvents = true;
      }
    }
  }

  ngOnInit(): void {
    this.scriptResources.loadResources();

    if (this.scriptId === '0') {
      this.scriptId = this.generateScriptId(5);
      console.log(`new script id:${this.scriptId}`);
      this.script = new Script(
        this.scriptId,
        'New Script',
        '',
        new Date(Date.parse('1970-01-01 00:00:00.000')),
      );

      this.scriptChannels = this.script.scriptChannels;
    } else {
      const ssObserver = {
        next: async (result: Script) => {
          for (const ch of result.scriptChannels) {
            ch.events = new Map<number, ScriptEvent>();

            for (const kvp of ch.eventsKvpArray) {
              ch.events.set(kvp.key, kvp.value);
            }
          }

          this.script = result;

          this.scriptChannels = this.script.scriptChannels;

          this.scriptChannels.sort((a, b) => this.channelCompare(a, b));

          while (!this.scriptResources.loaded) {
            await new Promise((r) => setTimeout(r, 100));
          }

          this.scriptResources.applyScript(this.script);
        },
        error: (err: unknown) => console.error(err),
      };

      this.scriptService.getScript(this.scriptId).subscribe(ssObserver);
    }
  }

  saveScript() {
    const observer = {
      next: (result: unknown) => {
        if (result && typeof result === 'object' && 'message' in result) {
          if (result.message === 'success') {
            console.log('script settings saved!');
            this.snackBar.okToast('Script settings saved!');
          }
        } else {
          console.log('script settings save failed!');
          this.snackBar.okToast('Script settings save failed!');
        }
      },
      error: (err: unknown) => {
        console.error(err);
        this.snackBar.okToast('Script settings save failed!');
      },
    };

    // Maps don't survive JSON.stringify
    for (const ch of this.script.scriptChannels) {
      ch.eventsKvpArray = [];

      for (const key of ch.events.keys()) {
        const val = ch.events.get(key);
        if (val !== undefined) {
          ch.eventsKvpArray.push({ key: key, value: val });
        }
      }
    }

    this.scriptService.saveScript(this.script).subscribe(observer);
  }

  //#region testing methods

  openScriptTestModal() {
    this.container.clear();

    const modalResources = new Map<string, unknown>();

    modalResources.set(ScriptTestModalResources.scriptId, this.scriptId);

    const locations = new Array<DeploymentLocation>();

    const locationDetails = this.scriptResources.getLocationDetailsList();

    locationDetails.forEach((detail) => {
      if (detail.assigned) {
        locations.push({
          id: detail.id,
          name: detail.name.toLocaleLowerCase(),
        });
      }
    });

    modalResources.set(ScriptTestModalResources.locations, locations);

    const component = this.container.createComponent(ScriptTestModalComponent);

    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
      this.modalCallback(evt);
    });
    this.components.push(component);

    this.backgroundClickDisabled = '1';

    this.modalService.open('scripter-modal');
  }

  saveBeforeTest() {
    const observer = {
      next: (result: unknown) => {
        if (result && typeof result === 'object' && 'message' in result) {
          if (result.message === 'success') {
            console.log('script settings saved!');
            this.openScriptTestModal();
          }
        } else {
          console.log('script settings save failed!');
          this.snackBar.okToast('Script settings save failed!');
        }
      },
      error: (err: unknown) => {
        console.error(err);
        this.snackBar.okToast('Script settings save failed!');
      },
    };

    // Maps don't survive JSON.stringify
    for (const ch of this.script.scriptChannels) {
      ch.eventsKvpArray = [];

      for (const key of ch.events.keys()) {
        const val = ch.events.get(key);
        if (val !== undefined) {
          ch.eventsKvpArray.push({ key: key, value: val });
        }
      }
    }

    this.scriptService.saveScript(this.script).subscribe(observer);
  }

  sendChannelTest(
    controllerId: number,
    commandType: ScriptChannelType,
    command: unknown,
  ) {
    const observer = {
      next: (result: unknown) => {
        if (result && typeof result === 'object' && 'message' in result) {
          if (result.message === 'success') {
            console.log('Test command sent!');
            this.snackBar.okToast('Test command sent!');
          }
        } else {
          console.log('Test command send failed!');
          this.snackBar.okToast('Test command send failed!');
        }
      },
      error: (err: unknown) => {
        console.error(err);
        this.snackBar.okToast('Test command send failed!');
      },
    };

    this.controllerService
      .sendControllerCommand(controllerId, commandType, command)
      .subscribe(observer);
  }

  //#endregion

  //#region channel modal

  openChannelAddModalKbd($event: KeyboardEvent) {
    if (
      $event.key === 'a' ||
      $event.key === 'A' ||
      $event.key === 'Enter' ||
      $event.key === ' '
    ) {
      this.openChannelAddModal();
    }
  }

  openChannelAddModal() {
    this.container.clear();

    const modalResources = new Map<string, unknown>();

    modalResources.set(
      AddChannelModalResources.controllers,
      this.scriptResources.getLocationDetailsList(),
    );

    modalResources.set(
      AddChannelModalResources.channels,
      this.scriptResources.getChannelDetailsMap(true),
    );

    const component = this.container.createComponent(AddChannelModalComponent);

    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
      this.modalCallback(evt);
    });
    this.components.push(component);

    this.modalService.open('scripter-modal');
  }

  //#endregion

  //#region event modal

  openNewEventModal(event: unknown) {
    const evt = event as MenuItemDetails;
    console.log(evt);

    let time = 0;

    const line = document.getElementById(`script-row-${evt.timeline}`);
    const scrollContainer = document.getElementById('scripter-container');

    if (line != null && scrollContainer != null) {
      const clickPos =
        (evt.xPos as number) + scrollContainer.scrollLeft - line.offsetLeft;

      time = Math.floor(clickPos / this.segmentWidth);

      const left = time * this.segmentWidth;

      if (Math.floor(clickPos) - left >= 30) {
        time += 1;
      }
    } else {
      this.snackBar.okToast('Could not determine event time!');
      console.log('could not determine event time');
      return;
    }

    const chIdx = this.scriptChannels
      .map((ch) => {
        return ch.id;
      })
      .indexOf(evt.timeline as string);

    const ch = this.scriptChannels[chIdx];

    const scriptEvent = new ScriptEvent(
      ch.id,
      ch.moduleChannel.moduleType,
      ch.moduleChannel.moduleSubType,
      time,
      undefined,
    );

    this.createEventModal(scriptEvent, false);
  }

  openEditEventModal(channelId: string, time: number) {
    const chIdx = this.scriptChannels
      .map((ch) => {
        return ch.id;
      })
      .indexOf(channelId);

    if (chIdx > -1) {
      const event = this.scriptChannels[chIdx].events.get(time);

      if (event) {
        this.createEventModal(event, true);
      }
    }
  }

  createEventModal(event: ScriptEvent, isEdit: boolean) {
    this.container.clear();

    const modalResources = new Map<string, unknown>();
    modalResources.set(ScriptEventModalResources.scriptEvent, event);

    if (isEdit) {
      modalResources.set(
        ScriptEventModalResources.callbackType,
        ScriptEventModalResources.editEvent,
      );
    }

    let component!: ComponentRef<ScripterModal>;

    switch (event.moduleType) {
      case ModuleType.uart:
        switch (event.moduleSubType) {
          case ModuleSubType.genericSerial: {
            component = this.container.createComponent(UartEventModalComponent);
            break;
          }
          case ModuleSubType.kangaroo: {
            component = this.container.createComponent(
              KangarooEventModalComponent,
            );
            modalResources.set(
              KangarooEventModalResources.kangaroo,
              event.scriptChannel,
            );
            break;
          }
          case ModuleSubType.humanCyborgRelationsSerial: {
            component = this.container.createComponent(
              HumanCyborgModalComponent,
            );
            break;
          }
          case ModuleSubType.maestro: {
            const scriptChannel = this.getScriptChannel(event.scriptChannel);
            if (!scriptChannel) {
              console.log(
                `could not find channel for event: ${JSON.stringify(event)}`,
              );
              this.snackBar.okToast('Error: Could not find channel for event!');
              return;
            }
            const mch = scriptChannel.moduleChannel as MaestroChannel;
            if (mch.isServo) {
              component = this.container.createComponent(
                ServoEventModalComponent,
              );
            } else {
              component = this.container.createComponent(
                GpioEventModalComponent,
              );
            }
          }
        }
        break;
      case ModuleType.i2c: {
        component = this.container.createComponent(I2cEventModalComponent);
        break;
      }
      case ModuleType.gpio: {
        component = this.container.createComponent(GpioEventModalComponent);
        break;
      }
    }

    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
      this.modalCallback(evt);
    });
    this.components.push(component);

    this.modalService.open('scripter-modal');
  }

  //#endregion

  modalCallback(evt: ModalCallbackEvent) {
    switch (evt.type) {
      case AddChannelModalResources.addChannelEvent: {
        const value = evt.value as AddChannelModalResponse;
        this.addChannel(value.channels);
        break;
      }
      case ConfirmModalResources.confirmEvent:
        this.handleConfirmModalEvent(evt.value as ConfirmModalEvent);
        break;
      case ScriptEventModalResources.addEvent: {
        const value = evt.value as ScriptEventModalResponse;
        this.addEvent(value.scriptEvent);
        break;
      }
      case ScriptEventModalResources.editEvent: {
        const value = evt.value as ScriptEventModalResponse;
        this.editEvent(value.scriptEvent, value.time);
        break;
      }
      case ScriptEventModalResources.removeEvent: {
        const value = evt.value as ScriptEventModalResponse;
        this.removeEvent(value.scriptEvent.scriptChannel, value.time);
        break;
      }
      case ChannelTestModalResources.channelTest: {
        const value = evt.value as ChannelTestModalResponse;
        this.sendChannelTest(
          value.controllerId,
          value.commandType,
          value.command,
        );
        break;
      }
    }

    this.modalService.close('scripter-modal');
    this.container.clear();
    this.components.splice(0, this.components.length);
    this.backgroundClickDisabled = '0';
  }

  handleConfirmModalEvent(evt: ConfirmModalEvent) {
    switch (evt.id) {
      case this.removeChannelEvent:
        this.removeChannel(evt.val as string);
        break;
    }
  }

  //#region script row callbacks

  timelineCallback(msg: TimeLineEvent) {
    msg.event.preventDefault();

    this.menuTopLeft.x = msg.event.clientX;
    this.menuTopLeft.y = msg.event.clientY;

    this.menuTrigger.menuData = {
      item: { timeline: msg.id, xPos: msg.event.clientX },
    };

    this.menuTrigger.openMenu();
  }

  removeCallback(msg: string) {
    this.container.clear();

    const modalResources = new Map<string, unknown>();
    modalResources.set(ConfirmModalResources.action, 'Delete');
    modalResources.set(
      ConfirmModalResources.message,
      'Are you sure you want to delete channel?',
    );
    modalResources.set(ConfirmModalResources.confirmEvent, {
      id: this.removeChannelEvent,
      val: msg,
    });

    const component = this.container.createComponent(ConfirmModalComponent);

    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
      this.modalCallback(evt);
    });

    this.components.push(component);

    this.modalService.open('scripter-modal');
  }

  private removeChannel(id: string) {
    const chIdx = this.scriptChannels
      .map((ch) => {
        return ch.id;
      })
      .indexOf(id);

    if (chIdx !== undefined && chIdx > -1) {
      const channel = this.scriptChannels[chIdx];

      this.scriptChannels.splice(chIdx, 1);

      this.scriptResources.setChannelAvailablity(
        channel.moduleChannelId,
        channel.channelType,
        true,
      );

      this.scriptChannels.sort((a, b) => this.channelCompare(a, b));
    }
  }

  channelTestCallback(msg: string) {
    const chIdx = this.scriptChannels
      .map((ch) => {
        return ch.id;
      })
      .indexOf(msg);

    if (chIdx !== undefined && chIdx > -1) {
      const ch = this.scriptChannels[chIdx];

      this.container.clear();

      const modalResources = new Map<string, unknown>();

      modalResources.set(
        ChannelTestModalResources.scriptChannelType,
        ch.channelType,
      );
      //modalResources.set(ChannelTestModalResources.channelId, ch.channelId);
      //modalResources.set(ChannelTestModalResources.controller, ch.locationId);

      const component = this.container.createComponent(
        ChannelTestModalComponent,
      );

      component.instance.resources = modalResources;
      component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
        this.modalCallback(evt);
      });
      this.components.push(component);

      this.modalService.open('scripter-modal');
    }
  }

  //#endregion

  //#region channel and event management

  getSelectableChannels(type: ScriptChannelType): ChannelDetails[] {
    const availableChannels = this.scriptResources.getChannelDetailsList(
      type,
      false,
    );
    if (availableChannels) {
      return availableChannels.sort((a, b) => a.name.localeCompare(b.name));
    }

    console.log(`no available channels for ${type}`);
    return [];
  }

  moduleChannelChanged(event: ModuleChannelChangedEvent) {
    const idx = this.scriptChannels.findIndex(
      (ch) => ch.id === event.channelId,
    );
    const swapIdx = this.scriptChannels.findIndex(
      (ch) => ch.moduleChannelId === event.newModuleChannelId,
    );

    const channel = this.scriptChannels[idx];

    const chValue = this.scriptResources.getScriptChannelResource(
      event.newModuleChannelId,
      channel.channelType,
    );

    if (chValue === undefined) {
      console.log(`no channel found for ${event.newModuleChannelId}`);
      return;
    }

    channel.parentModuleId = chValue.parentModuleId;
    channel.moduleChannelId = event.newModuleChannelId;
    channel.moduleChannel = chValue?.channel;
    this.scriptResources.setChannelAvailablity(
      channel.moduleChannelId,
      channel.channelType,
      false,
    );

    // if the new selected channel is already in use, swap it to the old channel
    if (swapIdx !== -1) {
      const swapChannel = this.scriptChannels[swapIdx];
      const oldChValue = this.scriptResources.getScriptChannelResource(
        event.oldModuleChannelId,
        swapChannel.channelType,
      );
      if (oldChValue === undefined) {
        console.log(`no channel found for ${event.oldModuleChannelId}`);
        return;
      }
      swapChannel.parentModuleId = oldChValue?.parentModuleId;
      swapChannel.moduleChannelId = event.oldModuleChannelId;
      swapChannel.moduleChannel = oldChValue?.channel;
      this.scriptResources.setChannelAvailablity(
        swapChannel.moduleChannelId,
        swapChannel.channelType,
        false,
      );
    } else {
      this.scriptResources.setChannelAvailablity(
        event.oldModuleChannelId,
        channel.channelType,
        true,
      );
    }
  }

  private addChannel(map: Map<ScriptChannelType, string[]>): void {
    for (const k of map.keys()) {
      const channels = map.get(k);

      if (!channels) {
        continue;
      }

      channels.forEach((channel) => {
        this.scriptResources.setChannelAvailablity(channel, k, false);

        const chValue = this.scriptResources.getScriptChannelResource(
          channel,
          k,
        );

        if (chValue === undefined) {
          return;
        }

        const ch = new ScriptChannel(
          Guid.create().toString(),
          this.script.id,
          k,
          chValue.parentModuleId,
          chValue.channelId,
          ModuleChannelTypes.fromSubType(chValue.channel.moduleSubType),
          chValue.channel,
          this.segments,
        );

        this.scriptChannels.push(ch);
      });
    }

    this.scriptChannels.sort((a, b) => this.channelCompare(a, b));
  }

  private addEvent(event: ScriptEvent): void {
    const chIdx = this.scriptChannels
      .map((ch) => {
        return ch.id;
      })
      .indexOf(event.scriptChannel);

    if (chIdx === -1) {
      console.log(`could not find channel for event: ${JSON.stringify(event)}`);
      this.snackBar.okToast('Error: Could not find channel for event!');
      return;
    }

    this.scriptChannels[chIdx].events.set(event.time, event);

    this.renderEvent(event);
  }

  private editEvent(event: ScriptEvent, oldTime: number) {
    //if (event.time !== oldTime) {
    this.removeEvent(event.scriptChannel, oldTime);
    this.addEvent(event);
    // }
    // else {

    //  const chIdx = this.scriptChannels
    //    .map((ch) => { return ch.id })
    //    .indexOf(event.scriptChannel);

    //  this.scriptChannels[chIdx].events.set(event.time, event);
    //}
  }

  private renderEvent(event: ScriptEvent) {
    //channelId: string, time: number) {
    const line = document.getElementById(`script-row-${event.scriptChannel}`);

    const floater = this.renderer.createElement('div');
    const displayText = EventMarkerHelper.generateText(event);

    const line1 = this.renderer.createElement('div');
    const line1txt = this.renderer.createText(displayText[0]);
    this.renderer.appendChild(line1, line1txt);
    this.renderer.appendChild(floater, line1);

    const line2 = this.renderer.createElement('div');
    const line2txt = this.renderer.createText(displayText[1]);
    this.renderer.appendChild(line2, line2txt);
    this.renderer.appendChild(floater, line2);

    const line3 = this.renderer.createElement('div');
    const line3txt = this.renderer.createText(displayText[2]);
    this.renderer.appendChild(line3, line3txt);
    this.renderer.appendChild(floater, line3);

    const line4 = this.renderer.createElement('div');
    const line4txt = this.renderer.createText(displayText[3]);
    this.renderer.appendChild(line4, line4txt);
    this.renderer.appendChild(floater, line4);

    this.renderer.setAttribute(floater, 'class', 'scripter-timeline-marker');
    this.renderer.setAttribute(
      floater,
      'id',
      `event-${event.scriptChannel}-${event.time}`,
    );
    this.renderer.setStyle(floater, 'top', `-5px`);
    this.renderer.setStyle(
      floater,
      'left',
      `${event.time * this.segmentWidth - 37}px`,
    );
    this.renderer.listen(floater, 'click', (_) => {
      this.openEditEventModal(event.scriptChannel, event.time);
    });
    this.renderer.appendChild(line, floater);
  }

  private removeEvent(channelId: string, time: number) {
    const chIdx = this.scriptChannels
      .map((ch) => {
        return ch.id;
      })
      .indexOf(channelId);

    this.scriptChannels[chIdx].events.delete(time);

    const element = document.getElementById(`event-${channelId}-${time}`);
    if (element) {
      element.parentNode?.removeChild(element);
    }
  }

  private channelCompare(a: ScriptChannel, b: ScriptChannel) {
    /* let val = a.locationId.localeCompare(b.locationId);
 
     if (val !== 0) {
       return val;
     }
 
     val = a.channelType - b.channelType;
 
     if (val !== 0) {
       return val;
     }
 */
    return a.moduleChannel.channelName < b.moduleChannel.channelName ? -1 : 1;
  }

  //#endregion

  //#region Drag scroll

  startDrag(event: MouseEvent) {
    event.preventDefault();
    this.isDragging = true;
    this.renderer.setStyle(
      this.scripterContainer.nativeElement,
      'cursor',
      'grabbing',
    );
    this.dragStartX =
      event.pageX - this.scripterContainer.nativeElement.offsetLeft;
    this.scrollLeft = this.scripterContainer.nativeElement.scrollLeft;
    this.dragStartY =
      event.pageY - this.scripterContainer.nativeElement.offsetTop;
    this.scrollTop = this.scripterContainer.nativeElement.scrollTop;
  }

  drag(event: MouseEvent) {
    if (!this.isDragging) return;
    event.preventDefault();
    const x = event.pageX - this.scripterContainer.nativeElement.offsetLeft;
    const walkX = x - this.dragStartX;
    this.scripterContainer.nativeElement.scrollLeft = this.scrollLeft - walkX;

    const y = event.pageY - this.scripterContainer.nativeElement.offsetTop;
    const walkY = y - this.dragStartY;
    this.scripterContainer.nativeElement.scrollTop = this.scrollTop - walkY;
  }

  endDrag() {
    this.isDragging = false;
    this.renderer.setStyle(
      this.scripterContainer.nativeElement,
      'cursor',
      'auto',
    );
  }

  //#endregion

  //#region utility methods

  private getScriptChannel(channelId: string): ScriptChannel | undefined {
    return this.scriptChannels.find((ch) => ch.id === channelId);
  }

  private generateScriptId(length: number): string {
    let result = `s${Math.floor(Date.now() / 1000)}`;
    const charactersLength = this.characters.length;
    for (let i = 0; i < length; i++) {
      result += this.characters.charAt(
        Math.floor(Math.random() * charactersLength),
      );
    }
    return result;
  }
  //#endregion
}

