      time: this.originalEventTime * this.timeFactor,
    });
    this.modalCallback.emit(evt);
  }

  closeModal() {
    const evt = new ModalCallbackEvent(
      ScriptEventModalResources.closeEvent,
      null,
    );

    this.modalCallback.emit(evt);
  }
}

