import {
  AfterContentInit,
  AfterViewInit,
  Component,
  ComponentRef,
  EventEmitter,
  Input,
  Output,
  ViewChild,
  ViewContainerRef,
} from '@angular/core';
import { FormsModule } from '@angular/forms';
import {
  MatExpansionPanel,
  MatExpansionPanelDescription,
  MatExpansionPanelHeader,
  MatExpansionPanelTitle,
} from '@angular/material/expansion';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { I2cModule, ModuleType, ModuleSubType } from 'astros-common';
import { RemoveModuleEvent } from '../../utility/module-events';
import { GenericI2cModuleComponent } from '../i2c-submodules/generic-i2c-module/generic-i2c-module.component';
import { Pca9685ModuleComponent } from '../i2c-submodules/pca9685-module/pca9685-module.component';
import { BaseI2cSubModuleComponent } from '../i2c-submodules/base-i2c-sub-module/base-i2c-sub-module.component';

export interface AddressChangeEvent {
  old: number;
  new: number;
}

@Component({
  selector: 'app-i2c-module',
  imports: [
    MatExpansionPanel,
    MatExpansionPanelHeader,
    MatExpansionPanelTitle,
    MatExpansionPanelDescription,
    FormsModule,
    FontAwesomeModule,
  ],
  templateUrl: './i2c-module.component.html',
  styleUrl: './i2c-module.component.scss',
})
export class I2cModuleComponent implements AfterViewInit, AfterContentInit {
  @ViewChild('i2cContainer', { read: ViewContainerRef })
  i2cContainer!: ViewContainerRef;

  @Input()
  module!: I2cModule;

  @Input()
  parentTestId!: string;

  // here to trigger change detection for object property changes
  @Input()
  set updateTrigger(val: number) {
    console.log('I2C Module Update Trigger: ', val);
    this.setModule();
  }

  @Output()
  removeModuleEvent = new EventEmitter<RemoveModuleEvent>();

  @Output()
  i2cAddressChangedEvent = new EventEmitter<AddressChangeEvent>();

  subtypeName = '';
  removeIcon = faTimes;
  component!: ComponentRef<unknown>;

  nameClicked(evt: MouseEvent) {
    evt.stopPropagation();
  }

  ngAfterViewInit(): void {
    this.setModule();
  }

  ngAfterContentInit(): void {
    switch (this.module.moduleSubType) {
      case ModuleSubType.genericI2C:
        this.subtypeName = 'Generic I2C';
        break;
      case ModuleSubType.humanCyborgRelationsI2C:
        this.subtypeName = 'Human Cyborg Relations';
        break;
      case ModuleSubType.pwmBoard:
        this.subtypeName = 'PCA9685 PWM Board';
        break;
      default:
        break;
    }
  }

  setModule() {
    if (this.module === undefined || this.module.moduleSubType === undefined) {
      return;
    }

    this.i2cContainer?.clear();

    let component!: ComponentRef<BaseI2cSubModuleComponent>;

    switch (this.module.moduleSubType) {
      case ModuleSubType.genericI2C:
        component = this.i2cContainer.createComponent(
          GenericI2cModuleComponent,
        ) as ComponentRef<GenericI2cModuleComponent>;
        break;
      case ModuleSubType.humanCyborgRelationsI2C:
      case ModuleSubType.pwmBoard:
        component = this.i2cContainer.createComponent(
          Pca9685ModuleComponent,
        ) as ComponentRef<Pca9685ModuleComponent>;
        break;
      default:
        break;
    }

    if (component) {
      component.instance.parentTestId = this.parentTestId;
      component.instance.module = this.module;
      component.instance.i2cAddressChangedEvent.subscribe((val: string) => {
        console.log('I2C Address Changed: ', val);

        this.i2cAddressChangedEvent.emit({
          old: this.module.i2cAddress,
          new: parseInt(val, 10),
        });
      });
    }
  }

  removeModule(event: Event) {
    event.stopPropagation();
    this.removeModuleEvent.emit({
      locationId: this.module.locationId,
      id: this.module.id,
      module: ModuleType.i2c,
    });
  }
}

import { Component, OnInit } from '@angular/core';
import {
  KangarooAction,
  KangarooX2,
  KangarooEvent,
  ScriptEvent,
} from 'astros-common';
import {
  BaseEventModalComponent,
  ScriptEventModalResources,
} from '../base-event-modal/base-event-modal.component';
import { FormsModule } from '@angular/forms';
import { DecimalPipe } from '@angular/common';
import { ModalCallbackEvent } from '../../modal-base/modal-callback-event';

export class KangarooEventModalResources {
  static kangaroo = 'kangaroo';
}

@Component({
  selector: 'app-kangaroo-event-modal',
  templateUrl: './kangaroo-event-modal.component.html',
  styleUrls: [
    '../base-event-modal/base-event-modal.component.scss',
    './kangaroo-event-modal.component.scss',
  ],
  imports: [FormsModule, DecimalPipe],
})
export class KangarooEventModalComponent
  extends BaseEventModalComponent
  implements OnInit
{
  kangaroo!: KangarooX2;

  channel1: string;
  ch1Action: string;
  ch1Speed?: number;
  ch1Position?: number;
  ch1SpdDisabled: boolean;
  ch1PosDisabled: boolean;

  channel2: string;
  ch2Action: string;
  ch2Speed?: number;
  ch2Position?: number;
  ch2SpdDisabled: boolean;
  ch2PosDisabled: boolean;

  constructor() {
    super();
    this.originalEventTime = 0;
    this.eventTime = 0;

    this.channel1 = 'Channel 1';
    this.channel2 = 'Channel 2';

    this.ch1Action = '0';
    this.ch2Action = '0';

    this.ch1SpdDisabled = true;
    this.ch1PosDisabled = true;

    this.ch2SpdDisabled = true;
    this.ch2PosDisabled = true;

    this.errorMessage = '';
    this.callbackType = ScriptEventModalResources.addEvent;
  }

  ngOnInit(): void {
    if (this.resources.has(ScriptEventModalResources.callbackType)) {
      this.callbackType = this.resources.get(
        ScriptEventModalResources.callbackType,
      ) as string;
    }

    if (this.callbackType === ScriptEventModalResources.editEvent) {
      const element = document.getElementById('remove_button');
      element?.classList.remove('hidden');
    }

    this.kangaroo = this.resources.get(
      KangarooEventModalResources.kangaroo,
    ) as KangarooX2;

    this.scriptEvent = this.resources.get(
      ScriptEventModalResources.scriptEvent,
    ) as ScriptEvent;

    const temp = this.scriptEvent.event as KangarooEvent;

    if (temp !== undefined) {
      this.ch1Action = temp.ch1Action.toString();
      this.ch1Speed = temp.ch1Speed;
      this.ch1Position = temp.ch1Position;

      this.ch2Action = temp.ch2Action.toString();
      this.ch2Speed = temp.ch2Speed;
      this.ch2Position = temp.ch2Position;
    }

    this.ch1SpdDisabled =
      +this.ch1Action !== KangarooAction.speed &&
      +this.ch1Action !== KangarooAction.position;
    this.ch1PosDisabled = +this.ch1Action !== KangarooAction.position;

    this.ch2SpdDisabled =
      +this.ch2Action !== KangarooAction.speed &&
      +this.ch2Action !== KangarooAction.position;
    this.ch2PosDisabled = +this.ch2Action !== KangarooAction.position;

    this.originalEventTime = this.scriptEvent.time / this.timeFactor;
    this.eventTime = this.scriptEvent.time / this.timeFactor;
  }

  modalChange($event: Event) {
    if (($event.target as HTMLInputElement).id === 'ch1select') {
      this.ch1SpdDisabled = +this.ch1Action !== 3 && +this.ch1Action !== 4;
      this.ch1PosDisabled = +this.ch1Action !== 4;

      if (+this.ch1Action !== 3 && +this.ch1Action !== 4) {
        this.ch1Speed = undefined;
      }
      if (+this.ch1Action !== 4) {
        this.ch1Position = undefined;
      }
    } else if (($event.target as HTMLInputElement).id === 'ch2select') {
      this.ch2SpdDisabled = +this.ch2Action !== 3 && +this.ch2Action !== 4;
      this.ch2PosDisabled = +this.ch2Action !== 4;

      if (+this.ch2Action !== 3 && +this.ch2Action !== 4) {
        this.ch2Speed = undefined;
      }
      if (+this.ch2Action !== 4) {
        this.ch2Position = undefined;
      }
    }
  }

  addEvent() {
    if (+this.eventTime > this.maxTime) {
      this.errorMessage = `Event time cannot be larger than ${this.maxTime / this.timeFactor}`;
      return;
    }

    this.scriptEvent.time = +this.eventTime * this.timeFactor;

    const data = new KangarooEvent(
      +this.ch1Action,
      this.ch1Speed ?? 0,
      this.ch1Position ?? 0,
      +this.ch2Action,
      this.ch2Speed ?? 0,
      this.ch2Position ?? 0,
    );

    this.scriptEvent.event = data;

    const evt = new ModalCallbackEvent(this.callbackType, {
      scriptEvent: this.scriptEvent,
      time: this.originalEventTime * this.timeFactor,
    });
    this.modalCallback.emit(evt);
  }
}

