import { ModuleSubType, ModuleType } from 'astros-common';

export interface AddModuleEvent {
  locationId: string;
  module: ModuleType;
}

export interface RemoveModuleEvent {
  locationId: string;
  id: string;
  module: ModuleType;
}

export interface ServoTestEvent {
  locationId: string;
  moduleType: ModuleType;
  moduleSubType: ModuleSubType;
  channelId: string;
}

import { AfterContentInit, Component } from '@angular/core';
import { ModalBaseComponent } from '../../modal-base/modal-base.component';
import { ModuleType } from 'astros-common/dist/astros_enums';
import { ModuleSubType } from 'astros-common/dist/astros_enums';
import { NgForOf } from '@angular/common';
import { ModalCallbackEvent } from '../../modal-base/modal-callback-event';
import { FormsModule } from '@angular/forms';

export class AddModuleModalResources {
  public static moduleType = 'moduleType';
  public static locationId = 'locationId';
  public static addEvent = 'add_module_addEvent';
  public static closeEvent = 'add_module_closeEvent';
}

export interface AddModuleModalResponse {
  locationId: string;
  moduleType: ModuleType;
  moduleSubType: ModuleSubType;
}

export interface ModuleSubTypeSelection {
  id: ModuleSubType;
  value: string;
}

@Component({
  selector: 'app-add-module-modal',
  imports: [NgForOf, FormsModule],
  templateUrl: './add-module-modal.component.html',
  styleUrl: './add-module-modal.component.scss',
})
export class AddModuleModalComponent
  extends ModalBaseComponent
  implements AfterContentInit
{
  options: ModuleSubTypeSelection[] = [];

  locationId = "0";
  moduleType: ModuleType = ModuleType.none;
  selectedSubType: ModuleSubType = ModuleSubType.none;

  moduleTypes: Map<ModuleType, string>;

  moduleSubTypes: Map<ModuleType, ModuleSubTypeSelection[]>;

  constructor() {
    super();

    this.moduleTypes = new Map<ModuleType, string>([
      [ModuleType.uart, 'Serial'],
      [ModuleType.i2c, 'I2C'],
      [ModuleType.gpio, 'GPIO'],
    ]);

    this.moduleSubTypes = new Map<ModuleType, ModuleSubTypeSelection[]>([
      [
        ModuleType.uart,
        [
          { id: ModuleSubType.genericSerial, value: 'Generic' },
          {
            id: ModuleSubType.humanCyborgRelationsSerial,
            value: 'Human Cyborg Relations',
          },
          { id: ModuleSubType.kangaroo, value: 'Kangaroo' },
          { id: ModuleSubType.maestro, value: 'Maestro' },
        ],
      ],
      [
        ModuleType.i2c,
        [
          { id: ModuleSubType.genericI2C, value: 'Generic' },
          {
            id: ModuleSubType.humanCyborgRelationsI2C,
            value: 'Human Cyborg Relations',
          },
          { id: ModuleSubType.pwmBoard, value: 'PWM Board' },
        ],
      ],
    ]);
  }

  ngAfterContentInit(): void {
    this.locationId = this.resources.get(
      AddModuleModalResources.locationId,
    ) as string;

    this.moduleType = this.resources.get(
      AddModuleModalResources.moduleType,
    ) as ModuleType;

    if (this.moduleSubTypes.has(this.moduleType)) {
      this.options = this.moduleSubTypes.get(
        this.moduleType,
      ) as ModuleSubTypeSelection[];
    }
  }

  modalChange($event: Event) {
    this.selectedSubType = +($event.target as HTMLInputElement).value;
  }

  addModule() {
    const evt = new ModalCallbackEvent(AddModuleModalResources.addEvent, {
      locationId: this.locationId,
      moduleType: this.moduleType,
      moduleSubType: this.selectedSubType,
    });
    this.modalCallback.emit(evt);
  }

  closeModal() {
    const evt = new ModalCallbackEvent(AddModuleModalResources.closeEvent, {});
    this.modalCallback.emit(evt);
  }
}

import {
  AfterContentInit,
  AfterViewInit,
  Component,
  ComponentRef,
  EventEmitter,
  Input,
  Output,
  ViewChild,
  ViewContainerRef,
} from '@angular/core';
import { FormsModule } from '@angular/forms';
import {
  MatExpansionPanel,
  MatExpansionPanelDescription,
  MatExpansionPanelHeader,
  MatExpansionPanelTitle,
} from '@angular/material/expansion';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { I2cModule, I2cType, ModuleType } from 'astros-common';
import { RemoveModuleEvent } from '../../utility/module-events';
import { GenericI2cModuleComponent } from '../i2c-submodules/generic-i2c-module/generic-i2c-module.component';
import { Pca9685ModuleComponent } from '../i2c-submodules/pca9685-module/pca9685-module.component';
import { BaseI2cSubModuleComponent } from '../i2c-submodules/base-i2c-sub-module/base-i2c-sub-module.component';

export interface AddressChangeEvent {
  old: number;
  new: number;
} 

@Component({
  selector: 'app-i2c-module',
  imports: [
    MatExpansionPanel,
    MatExpansionPanelHeader,
    MatExpansionPanelTitle,
    MatExpansionPanelDescription,
    FormsModule,
    FontAwesomeModule,
  ],
  templateUrl: './i2c-module.component.html',
  styleUrl: './i2c-module.component.scss',
})
export class I2cModuleComponent implements AfterViewInit, AfterContentInit {
  @ViewChild('i2cContainer', { read: ViewContainerRef })
  i2cContainer!: ViewContainerRef;

  @Input()
  module!: I2cModule;

  // here to trigger change detection for object property changes
  @Input()
  set updateTrigger(val: number) {
    console.log('I2C Module Update Trigger: ', val);
    this.setModule();
   }

  @Output()
  removeModuleEvent = new EventEmitter<RemoveModuleEvent>();

  @Output()
  i2cAddressChangedEvent = new EventEmitter<AddressChangeEvent>();

  subtypeName = '';
  removeIcon = faTimes;
  component!: ComponentRef<unknown>;

  nameClicked(evt: MouseEvent) {
    evt.stopPropagation();
  }

  ngAfterViewInit(): void {
    this.setModule();
  }

  ngAfterContentInit(): void {
    switch (this.module.type) {
      case I2cType.genericI2C:
        this.subtypeName = 'Generic I2C';
        break;
      case I2cType.humanCyborgRelations:
        this.subtypeName = 'Human Cyborg Relations';
        break;
      case I2cType.pwmBoard:
        this.subtypeName = 'PCA9685 PWM Board';
        break;
      default:
        break;
    }
  }

  setModule() {
    this.i2cContainer?.clear();

    let component!: ComponentRef<BaseI2cSubModuleComponent>;

    switch (this.module.type) {
      case I2cType.genericI2C:

        component = this.i2cContainer.createComponent(
          GenericI2cModuleComponent,
        ) as ComponentRef<GenericI2cModuleComponent>;
        break;
      case I2cType.humanCyborgRelations:
      case I2cType.pwmBoard:
        component = this.i2cContainer.createComponent(
          Pca9685ModuleComponent
        ) as ComponentRef<Pca9685ModuleComponent>;
        break;
      default:
        break;
    }

    if (component) {
      component.instance.module = this.module;
      component.instance.i2cAddressChangedEvent.subscribe((val: string) => {

        console.log('I2C Address Changed: ', val);

        this.i2cAddressChangedEvent.emit({
          old: this.module.i2cAddress,
          new: parseInt(val, 10),
        });
      });
    }
  }

  removeModule(event: Event) {
    event.stopPropagation();
    this.removeModuleEvent.emit({
      locationId: this.module.locationId,
      id: this.module.id,
      module: ModuleType.i2c,
    });
  }
}

import {
  AfterViewInit,
  Component,
  ElementRef,
  Renderer2,
  ViewChild,
  ViewContainerRef,
} from '@angular/core';
import {
  MatExpansionPanel,
  MatAccordion,
  MatExpansionPanelHeader,
  MatExpansionPanelTitle,
} from '@angular/material/expansion';
import {
  ControlModule,
  ControllerStatus,
  AstrOsLocationCollection,
  ControllerLocation,
  ModuleType,
  UartType,
  ModuleSubType,
  UartModule,
  HumanCyborgRelationsModule,
  KangarooX2,
  MaestroModule,
  I2cModule,
  I2cType,
  AstrOsConstants,
  MaestroBoard,
  MaestroChannel,
} from 'astros-common';
import { faExclamationTriangle } from '@fortawesome/free-solid-svg-icons';
import {
  AlertModalComponent,
  AlertModalResources,
  ConfirmModalComponent,
  ConfirmModalResources,
  ModalComponent,
} from '@src/components/modals';
import { ModalCallbackEvent } from '../../components/modals/modal-base/modal-callback-event';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { NgIf, NgFor } from '@angular/common';
import { FormsModule } from '@angular/forms';
import {
  AddModuleEvent,
  RemoveModuleEvent,
  ServoTestEvent,
} from '@src/components/esp-module/utility/module-events';
import { EspModuleComponent } from '@src/components/esp-module';
import {
  ControllerService,
  ModalService,
  SnackbarService,
  StatusService,
  WebsocketService,
} from '@src/services';
import {
  AddModuleModalComponent,
  AddModuleModalResources,
  LoadingModalComponent,
  LoadingModalResources,
  LoadingModalResponse,
  ServoTestModalComponent,
  ServoTestModalResources,
  ServoTestMessage,
  AddModuleModalResponse,
} from '@src/components/modals/modules';

interface Caption {
  str: string;
}

@Component({
  selector: 'app-modules',
  templateUrl: './modules.component.html',
  styleUrls: ['./modules.component.scss'],
  viewProviders: [MatExpansionPanel],
  imports: [
    MatAccordion,
    MatExpansionPanel,
    MatExpansionPanelHeader,
    MatExpansionPanelTitle,
    FontAwesomeModule,
    NgIf,
    FormsModule,
    EspModuleComponent,
    NgFor,
    ModalComponent,
  ],
})
export class ModulesComponent implements AfterViewInit {
  @ViewChild('modalContainer', { read: ViewContainerRef })
  container!: ViewContainerRef;

  isLoaded = false;

  backgroundClickDisabled = '1';

  possibleControllers: ControlModule[] = [];
  availableDomeControllers: ControlModule[] = [];
  availableCoreControllers: ControlModule[] = [];

  coreWarning = faExclamationTriangle;
  domeWarning = faExclamationTriangle;
  bodyWarning = faExclamationTriangle;

  @ViewChild('core', { static: false }) coreEl!: ElementRef;
  @ViewChild('dome', { static: false }) domeEl!: ElementRef;
  @ViewChild('body', { static: false }) bodyEl!: ElementRef;

  coreLocation!: ControllerLocation;
  domeLocation!: ControllerLocation;
  bodyLocation!: ControllerLocation;

  coreCaption: Caption = { str: 'Module Down' };
  domeCaption: Caption = { str: 'Module Down' };
  bodyCaption: Caption = { str: 'Module Down' };

  private notSynced = 'Not Synced';
  private moduleDown = 'Module Down';

  constructor(
    private controllerService: ControllerService,
    private websocketService: WebsocketService,
    private snackBar: SnackbarService,
    private modalService: ModalService,
    private renderer: Renderer2,
    private status: StatusService,
  ) {}

  ngAfterViewInit(): void {
    this.openControllerSyncModal();
  }

  //#region Loading Modal

  openControllerSyncModal() {
    this.container.clear();

    const modalResources = new Map<string, unknown>();

    const component = this.container.createComponent(LoadingModalComponent);

    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((result: ModalCallbackEvent) => {
      this.syncModalCallback(result);
    });

    this.modalService.open('modules-modal');
  }

  syncModalCallback(evt: ModalCallbackEvent) {
    if (evt.type !== LoadingModalResources.closeEvent) {
      return;
    }
    const response = evt.value as LoadingModalResponse;

    this.parseModules(response.locations);

    // always filter out the master controller since it's always the body module
    this.possibleControllers = response.controllers.filter(
      (controller: ControlModule) => controller.id !== 1,
    );

    this.availableCoreControllers = this.possibleControllers.filter(
      (controller: ControlModule) =>
        controller.id !== this.domeLocation.controller?.id,
    );
    this.availableDomeControllers = this.possibleControllers.filter(
      (controller: ControlModule) =>
        controller.id !== this.coreLocation.controller?.id,
    );

    this.handleStatus(
      this.status.getCoreStatus(),
      this.coreEl,
      this.coreCaption,
    );
    this.handleStatus(
      this.status.getDomeStatus(),
      this.domeEl,
      this.domeCaption,
    );
    this.handleStatus(
      this.status.getBodyStatus(),
      this.bodyEl,
      this.bodyCaption,
    );

    this.status.coreStateObserver.subscribe((value) =>
      this.handleStatus(value, this.coreEl, this.coreCaption),
    );
    this.status.domeStateObserver.subscribe((value) =>
      this.handleStatus(value, this.domeEl, this.domeCaption),
    );
    this.status.bodyStateObserver.subscribe((value) =>
      this.handleStatus(value, this.bodyEl, this.bodyCaption),
    );

    this.isLoaded = true;

    this.modalService.close('modules-modal');
  }
  //#endregion

  //#region Alert Modal
  openAlertModal(message: string) {
    this.container.clear();

    const modalResources = new Map<string, unknown>();

    const component = this.container.createComponent(AlertModalComponent);

    component.instance.resources = modalResources;
    component.instance.resources.set(AlertModalResources.message, message);

    component.instance.modalCallback.subscribe((_: unknown) => {
      this.modalService.close('modules-modal');
    });

    this.modalService.open('modules-modal');
  }
  //#endregion

  //#region Module Modals
  addModule(value: AddModuleEvent) {
    this.container.clear();

    const modalResources = new Map<string, unknown>();

    const component = this.container.createComponent(AddModuleModalComponent);

    component.instance.resources = modalResources;
    component.instance.resources.set(
      AddModuleModalResources.locationId,
      value.locationId,
    );
    component.instance.resources.set(
      AddModuleModalResources.moduleType,
      value.module,
    );

    component.instance.modalCallback.subscribe((result: ModalCallbackEvent) => {
      this.addModalCallback(result);
    });

    this.modalService.open('modules-modal');
  }

  addModalCallback(evt: ModalCallbackEvent) {
    if (evt.type === AddModuleModalResources.closeEvent) {
      this.modalService.close('modules-modal');
      this.container.clear();
      return;
    }

    if (evt.type !== AddModuleModalResources.addEvent) {
      return;
    }

    const response = evt.value as AddModuleModalResponse;
    this.doAddModule(response);

    this.modalService.close('modules-modal');
    this.container.clear();
  }

  removeModule(event: RemoveModuleEvent) {
    this.container.clear();
    const modalResources = new Map<string, unknown>();

    const component = this.container.createComponent(ConfirmModalComponent);

    component.instance.resources = modalResources;
    component.instance.resources.set(
      ConfirmModalResources.action,
      'Remove Module',
    );
    component.instance.resources.set(
      ConfirmModalResources.message,
      'Are you sure you want to remove this module?',
    );
    component.instance.resources.set(ConfirmModalResources.confirmEvent, event);

    component.instance.modalCallback.subscribe((result: ModalCallbackEvent) => {
      this.removeModuleCallback(result);
    });

    this.modalService.open('modules-modal');
  }

  removeModuleCallback(evt: ModalCallbackEvent) {
    console.log('evt', evt);
    if (evt.type === ConfirmModalResources.closeEvent) {
      this.modalService.close('modules-modal');
      this.container.clear();
      return;
    }

    if (evt.type !== ConfirmModalResources.confirmEvent) {
      return;
    }
    const response = evt.value as RemoveModuleEvent;

    switch (response.module) {
      case ModuleType.uart:
        this.removeUartModule(response.locationId, response.id);
        break;
      case ModuleType.i2c:
        this.removeI2CModule(response.locationId, response.id);
        break;
      case ModuleType.gpio:
        this.removeGPIOchannel(response.locationId, response.id);
        break;
    }

    this.modalService.close('modules-modal');
    this.container.clear();
  }

  //#endregion

  //#region Module logic

  doAddModule(response: AddModuleModalResponse) {
    switch (response.moduleType) {
      case ModuleType.uart:
        this.addUartModule(response.locationId, response.moduleSubType);
        break;
      case ModuleType.i2c:
        this.addI2CModule(response.locationId, response.moduleSubType);
        break;
      case ModuleType.gpio:
        this.addGPIOchannel(response.locationId, response.moduleSubType);
        break;
    }
  }

  //#region Serial Modules
  addUartModule(location: string, subType: ModuleSubType) {
    const controller = this.getControllerLocation(location);

    const defaultChannel = location === AstrOsConstants.BODY ? 2 : 1;
    const uartType = this.subtypeToUartType(subType);

    const module = new UartModule(
      crypto.randomUUID(),
      'New Serial Module',
      location,
      uartType,
      defaultChannel,
      9600,
    );

    switch (uartType) {
      case UartType.humanCyborgRelations:
        module.subModule = new HumanCyborgRelationsModule();
        break;
      case UartType.kangaroo:
        module.subModule = new KangarooX2(
          crypto.randomUUID(),
          'Channel 1',
          'Channel 2',
        );
        break;
      case UartType.maestro:
        module.subModule = this.generateMaestroModule();
        break;
    }

    controller.uartModules.push(module);
  }


  generateMaestroModule() {

    const subModule = new MaestroModule();

    subModule.boards = [
      new MaestroBoard(
        crypto.randomUUID(),
        0,
        'Board 1',
        24
      )
    ]

    for (let i = 0; i < 24; i++) {

      const idx = i + 1;
      subModule.boards[0].channels.push(
        new MaestroChannel(
          idx,
          `Channel ${i + idx}`,
          false,
          subModule.boards[0].id,
          true,
          500,
          2500,
          1250,
          false,
          0,
          0,
        )
      );
    }

    return subModule;
  }

  removeUartModule(location: string, moduleId: string) {
    const controller = this.getControllerLocation(location);

    controller.uartModules = controller.uartModules
      .filter((module: UartModule) => module.id !== moduleId)
      .sort((a, b) => a.name.localeCompare(b.name));
  }

  //#endregion

  //#region I2C Modules

  addI2CModule(location: string, subType: ModuleSubType) {
    const controller = this.getControllerLocation(location);
    const i2cType = this.subtypeToI2cType(subType);
    const nextAddress = this.getNextI2CAddress(controller.i2cModules);

    if (nextAddress === -1) {
      this.openAlertModal('All I2C addresses are in use.');
      return;
    }

    const module = new I2cModule(
      crypto.randomUUID(),
      'New I2C Module',
      location,
      nextAddress,
      i2cType,
    );

    switch (i2cType) {
      case I2cType.genericI2C:
        break;
      case I2cType.humanCyborgRelations:
        break;
      case I2cType.pwmBoard:
        break;
    }

    controller.i2cModules.push(module);
  }

  removeI2CModule(location: string, moduleId: string) {
    const controller = this.getControllerLocation(location);

    controller.i2cModules = controller.i2cModules
      .filter((module: I2cModule) => module.id !== moduleId)
      .sort((a, b) => a.name.localeCompare(b.name));
  }

  //#endregion

  addGPIOchannel(location: string, gpioType: ModuleSubType) {}

  removeGPIOchannel(location: string, channelId: string) {
    const controller = this.getControllerLocation(location);

    /*controller.gpioChannels = controller.gpioChannels.filter(
      (channel: string) => channel !== channelId,
    );
    */
  }

  //#endregion

  //#region Controller Selection

  controllerSelectChanged(_: unknown) {
    this.availableCoreControllers = this.possibleControllers.filter(
      (controller: ControlModule) =>
        controller.id !== this.domeLocation.controller?.id,
    );
    this.availableDomeControllers = this.possibleControllers.filter(
      (controller: ControlModule) =>
        controller.id !== this.coreLocation.controller?.id,
    );
  }

  //#endregion
  //#region Data Persistence

  saveModuleSettings() {
    const observer = {
      next: (result: unknown) => {
        if (result && typeof result === 'object' && 'message' in result) {
          if (result.message === 'success') {
            console.log('module settings saved!');
            this.snackBar.okToast('Module settings saved!');
          } else {
            console.log('module settings save failed!');
            this.snackBar.okToast('Module settings save failed!');
          }
        }
      },
      error: (err: unknown) => {
        console.error(err);
        this.snackBar.okToast('Module settings save failed!');
      },
    };

    this.controllerService
      .saveLocations(
        new AstrOsLocationCollection(
          this.coreLocation,
          this.domeLocation,
          this.bodyLocation,
        ),
      )
      .subscribe(observer);

    this.status.resetStatus();
  }

  syncModuleSettings() {
    const observer = {
      next: (result: unknown) => {
        if (result && typeof result === 'object' && 'message' in result) {
          if (result.message === 'success') {
            console.log('module sync queued!');
            this.snackBar.okToast('Module sync queued!');
          } else {
            console.log('module sync failed to queue');
            this.snackBar.okToast(`Module sync failed to queue.`);
          }
        }
      },
      error: (err: unknown) => {
        console.error(err);
        this.snackBar.okToast('Module sync failed!');
      },
    };

    this.controllerService.syncLocationConfig().subscribe(observer);
  }

  //#endregion

  //#region Servo Test

  openServoTestModal(value: ServoTestEvent) {
    if (!value.locationId) {
      this.openAlertModal('Location for this servo is not set.');
      return;
    }

    this.container.clear();

    const modalResources = new Map<string, unknown>();

    const component = this.container.createComponent(ServoTestModalComponent);

    component.instance.resources = modalResources;
    component.instance.resources.set(
      ServoTestModalResources.controllerId,
      value.locationId,
    );
    component.instance.resources.set(
      ServoTestModalResources.servoId,
      value.channelId,
    );

    component.instance.modalCallback.subscribe((result: ModalCallbackEvent) => {
      this.servoTestModalCallback(result);
    });

    this.modalService.open('modules-modal');
  }

  servoTestModalCallback(evt: ModalCallbackEvent) {
    switch (evt.type) {
      case ServoTestModalResources.sendServoMove: {
        const servoTest = evt.value as ServoTestMessage;
        this.websocketService.sendMessage({
          msgType: 'SERVO_TEST',
          data: {
            controllerId: servoTest.controllerId,
            servoId: servoTest.servoId,
            value: servoTest.value,
          },
        });
        break;
      }
      case ServoTestModalResources.closeEvent: {
        this.modalService.close('modules-modal');
        this.container.clear();
        break;
      }
    }
  }

  //#endregion

  //#region Helper Functions

  private parseModules(locations: AstrOsLocationCollection) {
    console.log(locations);
    try {
      this.coreLocation = locations.coreModule ?? this.coreLocation;
      this.domeLocation = locations.domeModule ?? this.domeLocation;
      this.bodyLocation = locations.bodyModule ?? this.bodyLocation;
    } catch (err) {
      console.error(err);
    }
  }

  private handleStatus(
    status: ControllerStatus,
    el: ElementRef,
    caption: Caption,
  ) {
    switch (status) {
      case ControllerStatus.up:
        caption.str = '';
        this.renderer.setStyle(el.nativeElement, 'visibility', 'hidden');
        break;
      case ControllerStatus.needsSynced:
        caption.str = this.notSynced;
        this.renderer.setStyle(el.nativeElement, 'color', 'yellow');
        this.renderer.setStyle(el.nativeElement, 'visibility', 'visible');
        break;
      case ControllerStatus.down:
        caption.str = this.moduleDown;
        this.renderer.setStyle(el.nativeElement, 'color', 'red');
        this.renderer.setStyle(el.nativeElement, 'visibility', 'visible');
        break;
    }
  }

  private getControllerLocation(locationId: string): ControllerLocation {
    switch (locationId) {
      case AstrOsConstants.BODY:
        return this.bodyLocation;
      case AstrOsConstants.CORE:
        return this.coreLocation;
      case AstrOsConstants.DOME:
        return this.domeLocation;
    }
    return this.bodyLocation;
  }

  private subtypeToUartType(subtype: ModuleSubType): UartType {
    switch (subtype) {
      case ModuleSubType.genericSerial:
        return UartType.genericSerial;
      case ModuleSubType.humanCyborgRelationsSerial:
        return UartType.humanCyborgRelations;
      case ModuleSubType.kangaroo:
        return UartType.kangaroo;
      case ModuleSubType.maestro:
        return UartType.maestro;
    }
    return UartType.genericSerial;
  }

  private subtypeToI2cType(subtype: ModuleSubType): I2cType {
    switch (subtype) {
      case ModuleSubType.genericI2C:
        return I2cType.genericI2C;
      case ModuleSubType.humanCyborgRelationsI2C:
        return I2cType.humanCyborgRelations;
      case ModuleSubType.pwmBoard:
        return I2cType.pwmBoard;
    }
    return I2cType.genericI2C;
  }

  private getNextI2CAddress(modules: I2cModule[]): number {
    const addresses = modules.map((module: I2cModule) => module.i2cAddress);
    for (let i = 0; i < 128; i++) {
      if (!addresses.includes(i)) {
        return i;
      }
    }
    return -1;
  }
}

