import { Component, OnInit } from '@angular/core';
import { ChannelType } from 'astros-common';
import { ChannelValue, LocationDetails } from 'src/app/models/script-resources';
import { ModalBaseComponent } from '../../modal-base/modal-base.component';
import { ModalCallbackEvent } from '../../modal-base/modal-callback-event';
import { FormsModule } from '@angular/forms';
import { NgFor, NgIf, KeyValuePipe } from '@angular/common';

export class ControllerModalResources {
  public static controllers = 'controllers';
  public static modules = 'modules';
  public static channels = 'channels';

  public static addChannelEvent = 'controller_addChannel';
  public static removeChannelEvent = 'controller_removeChannel';
  public static closeEvent = 'controller_close';
}

export interface ControllerModalResponse {
  controller: string;
  module: ChannelType;
  channels: number[];
}

@Component({
  selector: 'app-controller-modal',
  templateUrl: './controller-modal.component.html',
  styleUrls: ['./controller-modal.component.scss'],
  imports: [FormsModule, NgFor, NgIf, KeyValuePipe],
})
export class ControllerModalComponent
  extends ModalBaseComponent
  implements OnInit
{
  errorMessage: string;

  controllers!: Map<number, LocationDetails>;
  selectedController = 0;

  private availableModules!: Map<number, Map<ChannelType, string>>;
  modules: Map<ChannelType, string>;
  selectedModule: ChannelType = ChannelType.none;

  private availableChannels!: Map<number, Map<ChannelType, ChannelValue[]>>;
  channels: ChannelValue[];
  selectedChannel = -1;
  selectedChannels: unknown[] = [];

  constructor() {
    super();

    this.errorMessage = '';

    this.modules = new Map<ChannelType, string>();
    this.channels = new Array<ChannelValue>();
  }

  ngOnInit(): void {
    this.controllers = this.resources.get(
      ControllerModalResources.controllers,
    ) as Map<number, LocationDetails>;
    this.availableModules = this.resources.get(
      ControllerModalResources.modules,
    ) as Map<number, Map<ChannelType, string>>;
    this.availableChannels = this.resources.get(
      ControllerModalResources.channels,
    ) as Map<number, Map<ChannelType, ChannelValue[]>>;
  }

  modalChange($event: Event) {
    // convert from string value to number for enum
    if (($event.target as HTMLInputElement).id === 'controller-select') {
      this.setModules(+($event.target as HTMLInputElement).value);
    } else if (($event.target as HTMLInputElement).id === 'module-select') {
      this.setChannels(+($event.target as HTMLInputElement).value);
    }
  }

  addChannel() {
    if (
      +this.selectedController !== 4 &&
      +this.selectedModule === ChannelType.none
    ) {
      this.errorMessage = 'Module Selection Required';
      return;
    }

    if (
      +this.selectedModule !== ChannelType.none &&
      +this.selectedChannels.length < 1
    ) {
      this.errorMessage = 'Channel Selection Required';
      return;
    }

    const evt = new ModalCallbackEvent(
      ControllerModalResources.addChannelEvent,
      {
        controller: +this.selectedController,
        module:
          +this.selectedController === 4
            ? ChannelType.audio
            : +this.selectedModule,
        channels: this.selectedChannels,
      },
    );

    this.modalCallback.emit(evt);
    this.clearOptions();
  }

  closeModal() {
    this.clearOptions();
    const evt = new ModalCallbackEvent(
      ControllerModalResources.closeEvent,
      null,
    );
    this.modalCallback.emit(evt);
  }

  private clearOptions() {
    this.selectedController = 0;
    this.selectedModule = ChannelType.none;
    this.selectedChannel = -1;
    document
      .getElementById('module-select')
      ?.setAttribute('disabled', 'disabled');
    document
      .getElementById('channel-select')
      ?.setAttribute('disabled', 'disabled');
  }

  private setModules(controllerId: number) {
    if (controllerId === 4) {
      this.selectedModule = ChannelType.none;
      this.selectedChannel = -1;
      document
        .getElementById('module-select')
        ?.setAttribute('disabled', 'disabled');
      document
        .getElementById('channel-select')
        ?.setAttribute('disabled', 'disabled');
    } else {
      const mods = this.availableModules.get(+this.selectedController);
      if (mods) {
        this.modules = mods;
        document.getElementById('module-select')?.removeAttribute('disabled');
        //this.selectedModule = ChannelType.none;
        this.setChannels(this.selectedModule);
      }
    }
  }

  private setChannels(channelType: ChannelType) {
    if (channelType === ChannelType.none) {
      this.selectedChannel = -1;
      document
        .getElementById('channel-select')
        ?.setAttribute('disabled', 'disabled');
    } else {
      const chs = this.availableChannels
        .get(+this.selectedController)
        ?.get(+channelType);
      if (chs) {
        this.channels = chs;
        document.getElementById('channel-select')?.removeAttribute('disabled');
      }
    }
  }
}

import { Component, OnInit, ViewChild, ViewContainerRef } from '@angular/core';
import { faPlay, faTrash } from '@fortawesome/free-solid-svg-icons';
import { AudioFile } from 'astros-common';
import { AudioService } from 'src/app/services/audio/audio.service';
import { SnackbarService } from 'src/app/services/snackbar/snackbar.service';
import { NgFor, DatePipe } from '@angular/common';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import {
  ModalComponent,
  ConfirmModalComponent,
  ConfirmModalResources,
} from '@src/components/modals';
import {
  UploadModalComponent,
  UploadModalResources,
} from '@src/components/modals/utility';
import { ModalCallbackEvent } from '../../components/modals/modal-base/modal-callback-event';
import { ModalService } from '@src/services';

interface DeleteConfirmEvent {
  id: string;
  val: string;
}

@Component({
  selector: 'app-audio-files',
  templateUrl: './audio-files.component.html',
  styleUrls: ['./audio-files.component.scss'],
  imports: [NgFor, FontAwesomeModule, ModalComponent, DatePipe],
})
export class AudioFilesComponent implements OnInit {
  @ViewChild('modalContainer', { read: ViewContainerRef })
  container!: ViewContainerRef;

  faTrash = faTrash;
  faPlay = faPlay;

  audioFiles: AudioFile[];

  constructor(
    private snackBar: SnackbarService,
    private modalService: ModalService,
    private audioService: AudioService,
  ) {
    this.audioFiles = new Array<AudioFile>();
  }

  ngOnInit(): void {
    const observer = {
      next: (result: AudioFile[]) => (this.audioFiles = result),
      error: (err: unknown) => console.error(err),
    };

    this.audioService.getAudioFiles().subscribe(observer);
  }

  playFile(_: string) {
    this.snackBar.okToast('TODO: impelement this!');
  }

  uploadFile() {
    this.container.clear();

    const component = this.container.createComponent(UploadModalComponent);

    const modalResources = new Map<string, unknown>();
    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
      this.modalCallback(evt);
    });
    this.modalService.open('audio-files-modal');
  }

  removeFile(id: string) {
    this.container.clear();

    const idx = this.audioFiles
      .map((f) => {
        return f.id;
      })
      .indexOf(id);

    const fileName = this.audioFiles[idx].fileName;

    const modalResources = new Map<string, unknown>();
    modalResources.set(ConfirmModalResources.action, 'Confirm Delete');
    modalResources.set(
      ConfirmModalResources.message,
      `Are you sure you want to delete ${fileName}?`,
    );
    modalResources.set(ConfirmModalResources.confirmEvent, {
      id: 'audiofile_delete',
      val: id,
    });

    const component = this.container.createComponent(ConfirmModalComponent);

    component.instance.resources = modalResources;
    component.instance.modalCallback.subscribe((evt: ModalCallbackEvent) => {
      this.modalCallback(evt);
    });

    this.modalService.open('audio-files-modal');
  }

  modalCallback(evt: ModalCallbackEvent) {
    switch (evt.type) {
      case ConfirmModalResources.confirmEvent: {
        const val = evt.value as DeleteConfirmEvent;
        if (val.id === 'audiofile_delete') {
          this.remove(val.val as string);
        }
        break;
      }
      case UploadModalResources.refreshEvent:
        this.refreshAudioFiles();
        break;
    }

    this.modalService.close('audio-files-modal');
    this.container.clear();
  }

  refreshAudioFiles() {
    const observer = {
      next: (result: AudioFile[]) => (this.audioFiles = result),
      error: (err: unknown) => console.error(err),
    };

    this.audioService.getAudioFiles().subscribe(observer);
  }

  remove(id: string) {
    const observer = {
      next: (result: unknown) => {
        if (result && typeof result === 'object' && 'success' in result) {
          if (result.success) {
            const idx = this.audioFiles
              .map((f) => {
                return f.id;
              })
              .indexOf(id);

            this.audioFiles.splice(idx, 1);

            this.snackBar.okToast('File deleted!');
          } else {
            this.snackBar.okToast('File delete failed!');
          }
        }
      },
      error: (err: unknown) => {
        this.snackBar.okToast('File delete failed!');
        console.error(err);
      },
    };

    this.audioService.removeAudioFile(id).subscribe(observer);
  }
}

