@Component({
  selector: 'app-channel-test-modal',
  templateUrl: './channel-test-modal.component.html',
  styleUrls: [
    '../base-event-modal/base-event-modal.component.scss',
    './channel-test-modal.component.scss',
  ],
  imports: [NgIf, FormsModule],
})
export class ChannelTestModalComponent
  extends BaseEventModalComponent
  implements OnInit
{
  controllerId = 0;
  channelId = 0;

  channelType: ChannelType = ChannelType.none;
  channelSubType: ChannelSubType = ChannelSubType.none;

  speed = 1;
  position = 0;
  value = '';

  kangarooCh = 1;
  kangarooAction = 1;
  kangarooSpd?: number;
  kangarooPos?: number;
  spdDisabled = true;
  posDisabled = true;

  gpioLevel = 0;

  constructor() {
    super();
    this.callbackType = ChannelTestModalResources.channelTest;
  }

  ngOnInit(): void {
    this.controllerId = this.resources.get(
      ChannelTestModalResources.controller,
    ) as number;
    this.channelType = this.resources.get(
      ChannelTestModalResources.channelType,
    ) as ChannelType;
    this.channelSubType = this.resources.get(
      ChannelTestModalResources.channelSubType,
    ) as ChannelSubType;
    this.channelId = this.resources.get(
      ChannelTestModalResources.channelId,
    ) as number;
  }

  runClicked() {
    const evt = new ModalCallbackEvent(ChannelTestModalResources.channelTest, {
      controllerId: this.controllerId,
      commandType: this.channelType,
      command: this.getCommand(),
    });
    this.modalCallback.emit(evt);
  }

  getCommand(): unknown {
    switch (this.channelType) {
      case ChannelType.i2c:
        return { id: this.channelId, val: this.value };
      //case ChannelType.servo:
      //  return { id: this.channelId, position: this.position, speed: this.speed };
      case ChannelType.uart:
        if (this.channelSubType === ChannelSubType.kangaroo) {
          return { val: this.getKangarooCommand() };
        } else {
          return { val: this.value };
        }
      case ChannelType.gpio:
        return { id: this.channelId, val: this.gpioLevel };
    }
    return {};
  }

  selectChange($event: Event) {
    if (($event.target as HTMLInputElement).id === 'cmdselect') {
      this.spdDisabled =
        +this.kangarooAction !== 3 && +this.kangarooAction !== 4;
      this.posDisabled = +this.kangarooAction !== 4;

      if (+this.kangarooAction !== 3 && +this.kangarooAction !== 4) {
        this.kangarooSpd = 0;
      }
      if (+this.kangarooAction !== 4) {
        this.kangarooPos = 0;
      }
    }
  }

  getKangarooCommand(): string {
    let cmd = '';

    switch (+this.kangarooAction) {
      case KangarooAction.start:
        cmd = 'start';
        break;
      case KangarooAction.home:
        cmd = 'home';
        break;
      case KangarooAction.speed:
        cmd = `s${this.kangarooSpd === undefined ? 0 : this.kangarooSpd}`;
        break;
      case KangarooAction.position:
        cmd = `p${this.kangarooPos === undefined ? 0 : this.kangarooPos} s${this.kangarooSpd === undefined ? 0 : this.kangarooSpd}`;
        break;
    }

    return `${this.kangarooCh},${cmd}`;
  }
}

import {
  ChannelType,
  I2cChannel,
  Script,
  ControllerLocation,
  GpioChannel,
} from 'astros-common';
import { ModuleChannelType } from 'astros-common/dist/control_module/base_channel';

export class LocationDetails {
  id: string;
  name: string;

  constructor(id: string, name: string) {
    this.id = id;
    this.name = name;
  }
}

export class ChannelValue {
  available: boolean;
  channel: ModuleChannelType;

  constructor(channel: ModuleChannelType, available: boolean) {
    this.available = available;
    this.channel = channel;
  }
}

export class ScriptResources {
  locations: Map<string, LocationDetails>;

  uartChannels: Map<string, ChannelValue[]>;

  servoChannels: Map<string, ChannelValue[]>;

  i2cChannels: Map<string, ChannelValue[]>;

  gpioChannels: Map<string, ChannelValue[]>;

  constructor(locations: ControllerLocation[]) {
    this.locations = new Map<string, LocationDetails>();
    this.servoChannels = new Map<string, ChannelValue[]>();
    this.i2cChannels = new Map<string, ChannelValue[]>();
    this.uartChannels = new Map<string, ChannelValue[]>();
    this.gpioChannels = new Map<string, ChannelValue[]>();

    locations.forEach((loc) => {
      this.locations.set(loc.id, new LocationDetails(loc.id, loc.locationName));

      this.gpioChannels.set(
        loc.id,
        loc.gpioModule.channels.map(
          (ch: GpioChannel) => new ChannelValue(ch, ch.enabled),
        ),
      );

      this.uartChannels.get(loc.id)?.sort((a, b) => {
        return a.channel.id - b.channel.id;
      });
      this.servoChannels.get(loc.id)?.sort((a, b) => {
        return a.channel.id - b.channel.id;
      });
      this.i2cChannels.get(loc.id)?.sort((a, b) => {
        return a.channel.id - b.channel.id;
      });
      this.gpioChannels.get(loc.id)?.sort((a, b) => {
        return a.channel.id - b.channel.id;
      });
    });
  }

  applyScript(script: Script): void {
    script.scriptChannels.forEach((ch) => {
      switch (ch.type) {
        case ChannelType.uart:
          this.provisionChannel(
            this.uartChannels,
            ch.locationId,
            ch.channel.id,
          );
          break;
        case ChannelType.i2c:
          this.provisionChannel(this.i2cChannels, ch.locationId, ch.channel.id);
          break;
        case ChannelType.audio:
          break;
        case ChannelType.gpio:
          this.provisionChannel(
            this.gpioChannels,
            ch.locationId,
            ch.channel.id,
          );
          break;
      }
    });
  }

  getAvailableModules(): Map<number, Map<ChannelType, string>> {
    const result = new Map<number, Map<ChannelType, string>>();

    /*for (const ctrl of this.locations.keys()) {
      if (ctrl === 4 || ctrl === 0) {
        continue;
      }

      result.set(ctrl, this.setModuleValues(ctrl));
    }
  */
    return result;
  }

  getAvailableChannels(): Map<number, Map<ChannelType, ChannelValue[]>> {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const result = new Map<number, Map<ChannelType, any>>();

    /*
    for (const ctrl of this.locations.keys()) {
      if (ctrl === 4 || ctrl === 0) {
        continue;
      }
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      const vals = new Map<ChannelType, any>();

      vals.set(ChannelType.i2c, this.i2cChannels.get(ctrl));
      vals.set(ChannelType.uart, this.uartChannels.get(ctrl));
      vals.set(ChannelType.gpio, this.gpioChannels.get(ctrl));

      result.set(ctrl, vals);
    }
    */
    return result;
  }

  private setModuleValues(_: number): Map<ChannelType, string> {
    const vals = new Map<ChannelType, string>();

    vals.set(ChannelType.i2c, 'I2C');
    vals.set(ChannelType.uart, 'Serial');
    vals.set(ChannelType.gpio, 'GPIO');

    return vals;
  }

  addChannel(
    controller: string,
    type: ChannelType,
    id: number,
  ): ModuleChannelType | undefined {
    /*if (controller === 4) {
      this.locations.delete(4);
      return undefined;
    }
    */
    switch (type) {
      case ChannelType.uart:
        return this.provisionChannel(this.uartChannels, controller, id);
      case ChannelType.i2c:
        return this.provisionChannel(this.i2cChannels, controller, id);
      case ChannelType.gpio:
        return this.provisionChannel(this.gpioChannels, controller, id);
    }

    return undefined;
  }

  provisionChannel(
    map: Map<string, ChannelValue[]>,
    location: string,
    id: number,
  ): ModuleChannelType | undefined {
    const idx = map.get(location)?.findIndex((x) => x.channel.id === id);
    if (idx != undefined && idx > -1) {
      this.gpioChannels.get(location)![idx].available = false;
      return this.gpioChannels.get(location)![idx].channel;
    }

    return undefined;
  }

  removeChannel(location: string, type: ChannelType, id: number): void {
    switch (type) {
      case ChannelType.uart:
        this.deprovisionChannel(this.uartChannels, location, id);
        break;
      case ChannelType.i2c:
        this.deprovisionChannel(this.i2cChannels, location, id);
        break;
      case ChannelType.gpio:
        this.deprovisionChannel(this.gpioChannels, location, id);
        break;
    }
  }

  deprovisionChannel(
    map: Map<string, ChannelValue[]>,
    location: string,
    id: number,
  ): void {
    const idx = map.get(location)?.findIndex((x) => x.channel.id === id);
    if (idx !== undefined && idx > -1) {
      map.get(location)![idx].available = true;
    }
  }
}

