}

@Component({
  selector: 'app-controller-modal',
  templateUrl: './controller-modal.component.html',
  styleUrls: ['./controller-modal.component.scss'],
  imports: [FormsModule, NgFor, NgIf, KeyValuePipe],
})
export class ControllerModalComponent
  extends ModalBaseComponent
  implements OnInit
{
  errorMessage: string;

  controllers!: Map<number, LocationDetails>;
  selectedController = 0;

  private availableModules!: Map<number, Map<ChannelType, string>>;
  modules: Map<ChannelType, string>;
  selectedModule: ChannelType = ChannelType.none;

  private availableChannels!: Map<number, Map<ChannelType, ChannelValue[]>>;
  channels: ChannelValue[];
  selectedChannel = -1;
  selectedChannels: unknown[] = [];

  constructor() {
    super();

    this.errorMessage = '';

    this.modules = new Map<ChannelType, string>();
    this.channels = new Array<ChannelValue>();
  }

  ngOnInit(): void {
    this.controllers = this.resources.get(
      ControllerModalResources.controllers,
    ) as Map<number, LocationDetails>;
    this.availableModules = this.resources.get(
      ControllerModalResources.modules,
    ) as Map<number, Map<ChannelType, string>>;
    this.availableChannels = this.resources.get(
      ControllerModalResources.channels,
    ) as Map<number, Map<ChannelType, ChannelValue[]>>;
  }

  modalChange($event: Event) {
    // convert from string value to number for enum
    if (($event.target as HTMLInputElement).id === 'controller-select') {
      this.setModules(+($event.target as HTMLInputElement).value);
    } else if (($event.target as HTMLInputElement).id === 'module-select') {
      this.setChannels(+($event.target as HTMLInputElement).value);
    }
  }

  addChannel() {
    if (
      +this.selectedController !== 4 &&
      +this.selectedModule === ChannelType.none
    ) {
      this.errorMessage = 'Module Selection Required';
      return;
    }

    if (
      +this.selectedModule !== ChannelType.none &&
      +this.selectedChannels.length < 1
    ) {
      this.errorMessage = 'Channel Selection Required';
      return;
    }

    const evt = new ModalCallbackEvent(
      ControllerModalResources.addChannelEvent,
      {
        controller: +this.selectedController,
        module:
          +this.selectedController === 4
            ? ChannelType.audio
            : +this.selectedModule,
        channels: this.selectedChannels,
      },
    );

    this.modalCallback.emit(evt);
    this.clearOptions();
  }

  closeModal() {
    this.clearOptions();
    const evt = new ModalCallbackEvent(
      ControllerModalResources.closeEvent,
      null,
    );
    this.modalCallback.emit(evt);
  }

  private clearOptions() {
    this.selectedController = 0;
    this.selectedModule = ChannelType.none;
    this.selectedChannel = -1;
    document
      .getElementById('module-select')
      ?.setAttribute('disabled', 'disabled');
    document
      .getElementById('channel-select')
      ?.setAttribute('disabled', 'disabled');
  }

  private setModules(controllerId: number) {
    if (controllerId === 4) {
      this.selectedModule = ChannelType.none;
      this.selectedChannel = -1;
      document
        .getElementById('module-select')
        ?.setAttribute('disabled', 'disabled');
      document
        .getElementById('channel-select')
        ?.setAttribute('disabled', 'disabled');
    } else {
      const mods = this.availableModules.get(+this.selectedController);
      if (mods) {
        this.modules = mods;
        document.getElementById('module-select')?.removeAttribute('disabled');
        //this.selectedModule = ChannelType.none;
        this.setChannels(this.selectedModule);
      }
    }
  }

  private setChannels(channelType: ChannelType) {
    if (channelType === ChannelType.none) {
      this.selectedChannel = -1;
      document
        .getElementById('channel-select')
        ?.setAttribute('disabled', 'disabled');
    } else {
      const chs = this.availableChannels
        .get(+this.selectedController)
        ?.get(+channelType);
      if (chs) {
        this.channels = chs;
        document.getElementById('channel-select')?.removeAttribute('disabled');
      }
    }
  }
}

import {
  ChannelSubType,
  ChannelType,
  GenericSerialEvent,
  GpioEvent,
  HumanCyborgRelationsEvent,
  I2cEvent,
  KangarooAction,
  KangarooEvent,
  ScriptEvent,
} from 'astros-common';

export default class EventMarkerHelper {
  static generateText(event: ScriptEvent): string[] {
    switch (event.channelType) {
      case ChannelType.audio:
        return this.generateBasicResponse('Audio Track');
      //case ChannelType.servo:
      //    return this.servoText(event.dataJson);
      case ChannelType.i2c:
        return this.i2cText(event.dataJson);
      case ChannelType.gpio:
        return this.gpioText(event.dataJson);
      case ChannelType.uart:
        return this.uartText(event.channelSubType, event.dataJson);
      default:
        return this.generateBasicResponse('error');
    }
  }

  /*private static servoText(json: string): Array<string> {
        const evt = JSON.parse(json) as ServoEvent;
        const result = new Array<string>();
        result[0] = 'Position:';
        result[1] = evt.position.toString();
        result[2] = 'Speed:';
        result[3] = evt.speed.toString();

        return result;
    }*/

  private static i2cText(json: string): string[] {
    const evt = JSON.parse(json) as I2cEvent;
    const result = new Array<string>();
    result[0] = '\u00A0';
    result[1] = 'Message:';
    result[2] = evt.message;
    result[3] = '';

    return result;
  }

  private static gpioText(json: string): string[] {
    const evt = JSON.parse(json) as GpioEvent;
    const result = new Array<string>();
    result[0] = '\u00A0';
    result[1] = 'State:';
    result[2] = evt.setHigh ? 'High' : 'Low';
    result[3] = '';

    return result;
  }

  private static uartText(subType: ChannelSubType, json: string): string[] {
    switch (subType) {
      case ChannelSubType.genericSerial:
        return this.genericUart(json);
      case ChannelSubType.kangaroo:
        return this.kangaroo(json);
      case ChannelSubType.humanCyborgRelations:
        return this.humanCyborg(json);
      default:
        return this.generateBasicResponse('error');
    }
  }

  private static genericUart(json: string): string[] {
    const evt = JSON.parse(json) as GenericSerialEvent;
    const result = new Array<string>();
    result[0] = '\u00A0';
    result[1] = 'Message:';
    result[2] = evt.value;
    result[3] = '';

    return result;
  }

  static kangaroo(json: string): string[] {
    const evt = JSON.parse(json) as KangarooEvent;
    const result = new Array<string>();
    result[0] = 'CH 1:';
    result[1] = this.getKangarooActionName(evt.ch1Action);
    result[2] = 'CH 2:';
    result[3] = this.getKangarooActionName(evt.ch2Action);

    return result;
  }

  static getKangarooActionName(action: KangarooAction): string {
    switch (action) {
      case KangarooAction.start:
        return 'Start';
      case KangarooAction.home:
        return 'Home';
      case KangarooAction.position:
        return 'Position';
      case KangarooAction.speed:
        return 'Speed';
      case KangarooAction.none:
        return 'None';
      default:
        return 'error';
    }
  }

  static humanCyborg(json: string): string[] {
    const evt = JSON.parse(json) as HumanCyborgRelationsEvent;
    const result = new Array<string>();
    result[0] = '';
    result[1] = 'Event Count';
    result[2] = evt.commands.length.toString();
    result[3] = '';

    return result;
  }

  static generateBasicResponse(val: string): string[] {
    const result = [];
    result[0] = '\u00A0';
    result[1] = val;
    result[2] = '';
    result[3] = '';
    return result;
  }
}

