  HcrCommandCategory,
  HumanCyborgRelationsCmd,
  HumanCyborgRelationsEvent,
  HumanCyborgRelationsModule,
  ScriptEvent,
} from 'astros-common';
import { faBan } from '@fortawesome/free-solid-svg-icons';
