      }
      this.type = this.channel.isServo ? '1' : '2';
    }
  }

  onTypeChanged(evt: string): void {
    console.log(evt);
    switch (evt) {
      case '0':
        this.channel.enabled = false;
        break;
      case '1':
        this.channel.enabled = true;
        this.channel.isServo = true;
        break;
      case '2':
        this.channel.enabled = true;
        this.channel.isServo = false;
        break;
    }
  }

