import { Component, EventEmitter, Input, Output } from '@angular/core';
import { ModalCallbackEvent } from './modal-callback-event';

@Component({
  selector: 'app-modal-base',
  template: '',
  standalone: true,
  styleUrls: ['./modal-base.component.scss'],
})
export abstract class ModalBaseComponent {
  @Input()
  resources!: Map<string, unknown>;

  @Output() modalCallback = new EventEmitter<ModalCallbackEvent>();
}

import {
  Component,
  ElementRef,
  Input,
  OnInit,
  Renderer2,
  OnDestroy,
} from '@angular/core';
import { ModalService } from 'src/app/services/modal/modal.service';

@Component({
  selector: 'app-astros-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.scss'],
  standalone: true,
})
export class ModalComponent implements OnInit, OnDestroy {
  @Input() id: string;
  @Input() disableBackgroundClick: string;

  private element: ElementRef;

  constructor(
    private renderer: Renderer2,
    private modalService: ModalService,
    private el: ElementRef,
  ) {
    this.id = '';
    this.disableBackgroundClick = '';
    this.element = el;
  }

  ngOnInit(): void {
    if (!this.id || this.id === '') {
      console.error('modal must have an id');
      return;
    }

    this.renderer.appendChild(document.body, this.element.nativeElement);

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    this.element.nativeElement.addEventListener('click', (el: any) => {
      if (
        el.target.className === 'astros-modal-background' &&
        !+this.disableBackgroundClick
      ) {
        this.close();
      }
    });

    this.modalService.add(this);
  }

  ngOnDestroy(): void {
    this.modalService.remove(this.id);
    this.element.nativeElement.remove();
  }

  open(): void {
    this.element.nativeElement.style.display = 'block';
    document.body.classList.add('astros-modal-open');
  }

  close(): void {
    this.element.nativeElement.style.display = 'none';
    document.body.classList.remove('astro-modal-open');
  }
}

