import {
  AfterContentInit,
  AfterViewInit,
  Component,
  ComponentRef,
  EventEmitter,
  Input,
  Output,
  ViewChild,
  ViewContainerRef,
} from '@angular/core';
import {
  MatExpansionPanel,
  MatExpansionPanelDescription,
  MatExpansionPanelHeader,
  MatExpansionPanelTitle,
} from '@angular/material/expansion';
import { ModuleType, UartModule, UartType } from 'astros-common';
import { GenericSerialModuleComponent } from '../uart-submodules/generic-serial-module/generic-serial-module.component';
import { KangarooModuleComponent } from '../uart-submodules/kangaroo-module/kangaroo-module.component';
import { MaestroModuleComponent } from '../uart-submodules/maestro-module/maestro-module.component';
import { FormsModule } from '@angular/forms';
import { BaseUartSubModuleComponent } from '../uart-submodules/base-uart-sub-module/base-uart-sub-module.component';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { RemoveModuleEvent } from '../../utility/module-events';

@Component({
  selector: 'app-uart-module',
  templateUrl: './uart-module.component.html',
  styleUrl: './uart-module.component.scss',
  imports: [
    MatExpansionPanel,
    MatExpansionPanelHeader,
    MatExpansionPanelTitle,
    MatExpansionPanelDescription,
    FormsModule,
    FontAwesomeModule,
  ],
})
export class UartModuleComponent implements AfterViewInit, AfterContentInit {
  @ViewChild('uartContainer', { read: ViewContainerRef })
  uartContainer!: ViewContainerRef;

  @Input()
  isMaster = false;

  @Input()
  module!: UartModule;

  @Output()
  removeModuleEvent = new EventEmitter<RemoveModuleEvent>();

  subtypeName = '';
  removeIcon = faTimes;
  component!: ComponentRef<unknown>;

  nameClicked(evt: MouseEvent) {
    evt.stopPropagation();
  }

  ngAfterViewInit(): void {
    this.setModule();
  }

  ngAfterContentInit(): void {
    switch (this.module.uartType) {
      case UartType.genericSerial:
        this.subtypeName = 'Generic Serial';
        break;
      case UartType.kangaroo:
        this.subtypeName = 'Kangaroo X2';
        break;
      case UartType.maestro:
        this.subtypeName = 'Pololu Maestro';
        break;
      default:
        break;
    }
  }

  setModule() {
    this.uartContainer?.clear();

    let component!: ComponentRef<BaseUartSubModuleComponent>;

    switch (this.module.uartType) {
      case UartType.humanCyborgRelations:
      case UartType.genericSerial:
        component = this.uartContainer.createComponent(
          GenericSerialModuleComponent,
        ) as ComponentRef<GenericSerialModuleComponent>;
        break;
      case UartType.kangaroo:
        component = this.uartContainer.createComponent(
          KangarooModuleComponent,
        ) as ComponentRef<KangarooModuleComponent>;
        break;
      case UartType.maestro:
        component = this.uartContainer.createComponent(
          MaestroModuleComponent,
        ) as ComponentRef<MaestroModuleComponent>;
        break;
      default:
        break;
    }

    if (component) {
      component.instance.module = this.module;
      component.instance.isMaster = this.isMaster;
    }
  }

  removeModule(event: Event) {
    event.stopPropagation();
    this.removeModuleEvent.emit({
      locationId: this.module.locationId,
      id: this.module.id,
      module: ModuleType.uart,
    });
  }
}

import { HttpClient, HttpEventType } from '@angular/common/http';
import { Component } from '@angular/core';
import { faFileAudio, faTrash } from '@fortawesome/free-solid-svg-icons';
import { ModalBaseComponent } from '../../modal-base/modal-base.component';
import { ModalCallbackEvent } from '../../modal-base/modal-callback-event';
import { FileUpload } from 'src/app/models/upload-file';
import { NgFor, NgStyle, DecimalPipe } from '@angular/common';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';

export class UploadModalResources {
  public static message = 'message';
  public static refreshEvent = 'upload_refreshEvent';
  public static closeEvent = 'upload_closeEvent';
}

@Component({
  selector: 'app-upload-modal',
  templateUrl: './upload-modal.component.html',
  styleUrls: ['./upload-modal.component.scss'],
  imports: [NgFor, NgStyle, FontAwesomeModule, DecimalPipe],
})
export class UploadModalComponent extends ModalBaseComponent {
  faTrash = faTrash;
  faFiles = faFileAudio;

  uploadQueue: FileUpload[];
  isUploading: boolean;

  path = '/api/audio/savefile';

  response: string;

  private token: string;

  constructor(private http: HttpClient) {
    super();

    this.response = '';

    this.token = localStorage.getItem('astros-token') || '';
    this.uploadQueue = new Array<FileUpload>();
    this.isUploading = false;
  }

  removeFile(toRemove: unknown) {
    let filename = '';

    if (toRemove && typeof toRemove === 'object' && 'name' in toRemove) {
      filename = toRemove.name as string;
    } else {
      filename = toRemove as string;
    }

    const idx = this.uploadQueue
      .map((file) => {
        if (typeof file.fileData === 'object' && 'name' in file.fileData) {
          return file.fileData.name;
        }
        return file.fileData;
      })
      .indexOf(filename);

    this.uploadQueue.splice(idx, 1);
  }

  addToQueue(evt: Event) {
    const files = (evt.target as HTMLInputElement).files;

    if (!files) {
      return;
    }

    for (let i = 0; i < files.length; i++) {
      const file = files.item(i);
      if (file !== null) {
        this.uploadQueue.push(new FileUpload(file));
      }
    }
  }

  uploadAll() {
    this.isUploading = true;

    for (const file of this.uploadQueue) {
      const formData = new FormData();

      formData.append('file', file.fileData);

      file.subscription = this.http
        .post(this.path, formData, {
          reportProgress: true,
          observe: 'events',
        })
        .subscribe((evt) => {
          if (evt.type === HttpEventType.UploadProgress && evt.total) {
            file.uploadProgress = Math.round(100 * (evt.loaded / evt.total));
          }
        });
    }
  }

  cancelAll() {
    for (const file of this.uploadQueue) {
      file.subscription?.unsubscribe();
    }
    this.isUploading = true;
  }

  reset() {
    for (const file of this.uploadQueue) {
      file.subscription?.unsubscribe();
      file.subscription = undefined;
      file.uploadProgress = undefined;
    }

    this.uploadQueue.splice(0, this.uploadQueue.length);
    this.isUploading = false;
  }

  closeModal() {
    const evt = new ModalCallbackEvent(UploadModalResources.refreshEvent, null);
    this.modalCallback.emit(evt);
  }
}

